import { defineStore } from 'pinia'
import { ref } from 'vue'
import { 
  getTemplate as apiGetTemplate, 
  saveTemplate as apiSaveTemplate, 
  publishTemplate as apiPublishTemplate 
} from '@/api/resume'

export const useTemplateStore = defineStore('template-editor', () => {
  // 状态
  const currentTemplate = ref(null)
  const loading = ref(false)
  const error = ref(null)

  // actions
  const getTemplate = async (id) => {
    try {
      loading.value = true
      const response = await apiGetTemplate(id)
      currentTemplate.value = response.data
      return response.data
    } catch (err) {
      error.value = err.message
      throw err
    } finally {
      loading.value = false
    }
  }

  const saveTemplate = async (templateData) => {
    try {
      loading.value = true
      const response = await apiSaveTemplate(templateData)
      currentTemplate.value = response.data
      return response.data
    } catch (err) {
      error.value = err.message
      throw err
    } finally {
      loading.value = false
    }
  }

  const publishTemplate = async (templateId) => {
    try {
      loading.value = true
      const response = await apiPublishTemplate(templateId)
      currentTemplate.value = response.data
      return response.data
    } catch (err) {
      error.value = err.message
      throw err
    } finally {
      loading.value = false
    }
  }

  return {
    currentTemplate,
    loading,
    error,
    getTemplate,
    saveTemplate,
    publishTemplate
  }
})
