import { ref, computed } from 'vue';
import { useRoute } from 'vue-router';
import MobileTabBar from '@/components/MobileTabBar.vue';
import PageBanner from '@/components/common/PageBanner.vue';
import CategoryMenu from '@/components/CategoryMenu.vue';
import ResumeNavigation from '@/components/resume/ResumeNavigation.vue';
import { useResumeData } from '@/composables/useResumeData';
import { ChevronRightIcon, EyeIcon, HeartIcon, HomeIcon, AcademicCapIcon, BriefcaseIcon, UserGroupIcon, SparklesIcon, LanguageIcon } from '@heroicons/vue/24/outline';
export default {
  __name: 'TemplatesView',
  setup(__props, {
    expose: __expose
  }) {
    __expose();
    const route = useRoute();

    // 分类相关状态
    const currentMainCategory = ref('all');
    const currentSubCategory = ref('');
    const currentThirdCategory = ref('');
    const currentLevel = ref('main');
    const showCategoryMenu = ref(false);

    // 排序相关状态
    const currentSort = ref('popular');

    // 分类数据
    const categories = computed(() => [{
      id: 'all',
      name: '全部',
      icon: HomeIcon
    }, {
      id: 'fresh',
      name: '应届生',
      icon: AcademicCapIcon
    }, {
      id: 'experienced',
      name: '经验型',
      icon: BriefcaseIcon
    }, {
      id: 'manager',
      name: '管理层',
      icon: UserGroupIcon
    }, {
      id: 'creative',
      name: '创意型',
      icon: SparklesIcon
    }, {
      id: 'international',
      name: '国际化',
      icon: LanguageIcon
    }]);

    // 获取模板数据
    const {
      templates
    } = useResumeData();

    // 过滤模板列表
    const filteredTemplates = computed(() => {
      let result = [...templates.value];

      // 应用分类过滤
      if (currentMainCategory.value !== 'all') {
        result = result.filter(template => template.category === currentMainCategory.value);
      }

      // 应用排序
      switch (currentSort.value) {
        case 'popular':
          result.sort((a, b) => b.views + b.likes - (a.views + a.likes));
          break;
        case 'newest':
          result.sort((a, b) => new Date(b.date) - new Date(a.date));
          break;
        case 'downloads':
          result.sort((a, b) => b.downloads - a.downloads);
          break;
        case 'likes':
          result.sort((a, b) => b.likes - a.likes);
          break;
      }
      return result;
    });

    // 获取当前分类名称
    const currentCategoryName = computed(() => {
      if (currentMainCategory.value === 'all') return '全部';
      const category = categories.value.find(c => c.id === currentMainCategory.value);
      return category?.name || '';
    });

    // 获取模板分类名称
    const getTemplateCategoryName = categoryId => {
      const category = categories.value.find(c => c.id === categoryId);
      return category?.name || categoryId;
    };

    // 处理分类切换
    const handleCategoryChange = (categoryId, level = 'main') => {
      if (level === 'main') {
        currentMainCategory.value = categoryId;
        currentSubCategory.value = '';
        currentThirdCategory.value = '';
        if (categoryId !== 'all') {
          currentLevel.value = 'sub';
        } else {
          showCategoryMenu.value = false;
        }
      } else if (level === 'sub') {
        currentSubCategory.value = categoryId;
        currentThirdCategory.value = '';
        showCategoryMenu.value = false;
      }
    };
    const __returned__ = {
      route,
      currentMainCategory,
      currentSubCategory,
      currentThirdCategory,
      currentLevel,
      showCategoryMenu,
      currentSort,
      categories,
      templates,
      filteredTemplates,
      currentCategoryName,
      getTemplateCategoryName,
      handleCategoryChange,
      ref,
      computed,
      get useRoute() {
        return useRoute;
      },
      MobileTabBar,
      PageBanner,
      CategoryMenu,
      ResumeNavigation,
      get useResumeData() {
        return useResumeData;
      },
      get ChevronRightIcon() {
        return ChevronRightIcon;
      },
      get EyeIcon() {
        return EyeIcon;
      },
      get HeartIcon() {
        return HeartIcon;
      },
      get HomeIcon() {
        return HomeIcon;
      },
      get AcademicCapIcon() {
        return AcademicCapIcon;
      },
      get BriefcaseIcon() {
        return BriefcaseIcon;
      },
      get UserGroupIcon() {
        return UserGroupIcon;
      },
      get SparklesIcon() {
        return SparklesIcon;
      },
      get LanguageIcon() {
        return LanguageIcon;
      }
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
