import { defineStore } from 'pinia'
import { ref, computed } from 'vue'
import { getComponents } from '@/api/resume'

export const useComponentStore = defineStore('template-components', () => {
  // 状态
  const components = ref([])
  const loading = ref(false)
  const error = ref(null)

  // 组件分类
  const componentCategories = computed(() => {
    const categories = {}
    components.value.forEach(component => {
      if (!categories[component.category]) {
        categories[component.category] = []
      }
      categories[component.category].push(component)
    })
    return categories
  })

  // 获取组件列表
  const loadComponents = async () => {
    try {
      loading.value = true
      const response = await getComponents()
      components.value = response.data
    } catch (err) {
      error.value = err.message
      throw err
    } finally {
      loading.value = false
    }
  }

  // 根据类型获取组件
  const getComponentByType = (type) => {
    return components.value.find(c => c.type === type)
  }

  // 获取组件配置
  const getComponentConfig = (type) => {
    const component = getComponentByType(type)
    return component ? component.config : null
  }

  // 获取组件模板
  const getComponentTemplate = (type) => {
    const component = getComponentByType(type)
    return component ? component.template : null
  }

  return {
    components,
    loading,
    error,
    componentCategories,
    loadComponents,
    getComponentByType,
    getComponentConfig,
    getComponentTemplate
  }
})
