import "core-js/modules/esnext.iterator.constructor.js";
import "core-js/modules/esnext.iterator.find.js";
import { ref, computed } from 'vue';
import { useRoute } from 'vue-router';
import HeadView from '@/components/HeadView.vue';
import FootView from '@/components/FootView.vue';
import CategoryMenu from '@/components/CategoryMenu.vue';
import { ChevronRightIcon } from '@heroicons/vue/24/outline';
import { HomeIcon, CodeBracketIcon, ServerIcon, DevicePhoneMobileIcon, SparklesIcon, CogIcon, QuestionMarkCircleIcon, CheckCircleIcon, StarIcon, GiftIcon, FireIcon, BoltIcon, BriefcaseIcon, WrenchScrewdriverIcon, UserGroupIcon, ClockIcon } from '@heroicons/vue/24/outline';
export default {
  __name: 'CommunityView',
  setup(__props, {
    expose: __expose
  }) {
    __expose();
    const route = useRoute();

    // 分类相关状态
    const currentMainCategory = ref('all');
    const currentSubCategory = ref('');
    const currentThirdCategory = ref('');
    const currentLevel = ref('main');
    const showCategoryMenu = ref(false);

    // 分类数据
    const categories = computed(() => {
      const path = route.path;
      if (path.includes('articles')) {
        return [{
          id: 'all',
          name: '全部',
          icon: HomeIcon
        }, {
          id: 'frontend',
          name: '前端开发',
          icon: CodeBracketIcon
        }, {
          id: 'backend',
          name: '后端开发',
          icon: ServerIcon
        }, {
          id: 'mobile',
          name: '移动开发',
          icon: DevicePhoneMobileIcon
        }, {
          id: 'ai',
          name: '人工智能',
          icon: SparklesIcon
        }, {
          id: 'devops',
          name: 'DevOps',
          icon: CogIcon
        }];
      } else if (path.includes('questions')) {
        return [{
          id: 'all',
          name: '全部',
          icon: HomeIcon
        }, {
          id: 'unsolved',
          name: '待解决',
          icon: QuestionMarkCircleIcon
        }, {
          id: 'solved',
          name: '已解决',
          icon: CheckCircleIcon
        }, {
          id: 'featured',
          name: '精选问答',
          icon: StarIcon
        }, {
          id: 'reward',
          name: '悬赏问答',
          icon: GiftIcon
        }];
      } else if (path.includes('topics')) {
        return [{
          id: 'all',
          name: '全部',
          icon: HomeIcon
        }, {
          id: 'hot',
          name: '热门话题',
          icon: FireIcon
        }, {
          id: 'tech',
          name: '技术之争',
          icon: BoltIcon
        }, {
          id: 'career',
          name: '职业发展',
          icon: BriefcaseIcon
        }, {
          id: 'tools',
          name: '工具推荐',
          icon: WrenchScrewdriverIcon
        }];
      }
      // 首页分类
      return [{
        id: 'all',
        name: '全部',
        icon: HomeIcon
      }, {
        id: 'recommend',
        name: '推荐',
        icon: SparklesIcon
      }, {
        id: 'following',
        name: '关注',
        icon: UserGroupIcon
      }, {
        id: 'latest',
        name: '最新',
        icon: ClockIcon
      }];
    });

    // 获取当前分类名称
    const currentCategoryName = computed(() => {
      if (currentMainCategory.value === 'all') return '全部';
      const category = categories.value.find(c => c.id === currentMainCategory.value);
      return category?.name || '';
    });

    // 处理分类切换
    const handleCategoryChange = (categoryId, level = 'main') => {
      if (level === 'main') {
        currentMainCategory.value = categoryId;
        currentSubCategory.value = '';
        currentThirdCategory.value = '';
        if (categoryId !== 'all') {
          currentLevel.value = 'sub';
        } else {
          showCategoryMenu.value = false;
        }
      } else if (level === 'sub') {
        currentSubCategory.value = categoryId;
        currentThirdCategory.value = '';
        showCategoryMenu.value = false;
      }
    };

    // 添加新的图标导入

    const __returned__ = {
      route,
      currentMainCategory,
      currentSubCategory,
      currentThirdCategory,
      currentLevel,
      showCategoryMenu,
      categories,
      currentCategoryName,
      handleCategoryChange,
      ref,
      computed,
      get useRoute() {
        return useRoute;
      },
      HeadView,
      FootView,
      CategoryMenu,
      get ChevronRightIcon() {
        return ChevronRightIcon;
      },
      get HomeIcon() {
        return HomeIcon;
      },
      get CodeBracketIcon() {
        return CodeBracketIcon;
      },
      get ServerIcon() {
        return ServerIcon;
      },
      get DevicePhoneMobileIcon() {
        return DevicePhoneMobileIcon;
      },
      get SparklesIcon() {
        return SparklesIcon;
      },
      get CogIcon() {
        return CogIcon;
      },
      get QuestionMarkCircleIcon() {
        return QuestionMarkCircleIcon;
      },
      get CheckCircleIcon() {
        return CheckCircleIcon;
      },
      get StarIcon() {
        return StarIcon;
      },
      get GiftIcon() {
        return GiftIcon;
      },
      get FireIcon() {
        return FireIcon;
      },
      get BoltIcon() {
        return BoltIcon;
      },
      get BriefcaseIcon() {
        return BriefcaseIcon;
      },
      get WrenchScrewdriverIcon() {
        return WrenchScrewdriverIcon;
      },
      get UserGroupIcon() {
        return UserGroupIcon;
      },
      get ClockIcon() {
        return ClockIcon;
      }
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
