import request from '@/utils/request'

export const account = {
  // 更新密码
  updatePassword: (data) => {
    return request({
      url: '/api/v1/users/account/password/',
      method: 'post',
      data
    })
  },

  // 更换手机号
  changePhone: (data) => {
    return request.post('/api/v1/users/account/phone/', data)
  },

  // 更换邮箱
  updateEmail: (data) => {
    return request.post('/api/v1/users/account/email/', data)
  },

  // 注销账号
  deleteAccount: (data) => {
    return request({
      url: '/api/v1/users/account/delete/',
      method: 'POST',
      data
    })
  },

  // 发送验证码
  sendVerifyCode: (data) => {
    return request({
      url: '/api/v1/users/auth/sms/send/',
      method: 'post',
      data: {
        phone: data.phone,
        scene: data.scene
      }
    })
  }
}

export default account
