import request from '@/utils/request'

export default {
  // 获取积分信息
  getPointsInfo() {
    return request({
      url: '/api/v1/membership/points/balance/',
      method: 'get'
    })
  },

  // 获取积分记录
  getPointsRecords(params) {
    return request({
      url: '/api/v1/membership/points/records/',
      method: 'get',
      params
    })
  },

  // 签到
  signIn() {
    return request({
      url: '/api/v1/membership/points/check-in/',
      method: 'post'
    })
  },

  // 获取签到状态
  getSignInStatus() {
    return request({
      url: '/api/v1/membership/points/check-in/status/',
      method: 'get'
    })
  }
}
