import { ref, computed } from 'vue';
import { useRoute, useRouter } from 'vue-router';
import HeadView from '@/components/HeadView.vue';
import FooterView from '@/components/FooterView.vue';
import { DocumentTextIcon, QuestionMarkCircleIcon, ScaleIcon, UserIcon, Bars3Icon, XMarkIcon, PlusIcon, DocumentIcon, EnvelopeIcon, Cog6ToothIcon, UserCircleIcon, PaintBrushIcon, VideoCameraIcon, CameraIcon, FilmIcon, UsersIcon } from '@heroicons/vue/24/outline';
export default {
  __name: 'CommunityView',
  setup(__props, {
    expose: __expose
  }) {
    __expose();
    const route = useRoute();
    const router = useRouter();
    const isMobileMenuOpen = ref(false);
    const unreadMessagesCount = ref(0);
    const messageBadge = computed(() => unreadMessagesCount.value);

    // 更多菜单中的选项分组
    const menuGroups = [{
      title: '社区',
      items: [{
        name: '文章',
        path: '/community/articles',
        icon: DocumentTextIcon
      }, {
        name: '问答',
        path: '/community/questions',
        icon: QuestionMarkCircleIcon
      }, {
        name: '话题',
        path: '/community/topics',
        icon: ScaleIcon
      }]
    }, {
      title: '创作集',
      items: [{
        name: '设计',
        path: '/portfolio/design',
        icon: PaintBrushIcon
      }, {
        name: '视频',
        path: '/portfolio/video',
        icon: VideoCameraIcon
      }, {
        name: '摄影',
        path: '/portfolio/photography',
        icon: CameraIcon
      }, {
        name: '动画',
        path: '/portfolio/animation',
        icon: FilmIcon
      }]
    }];

    // 移动端底部导航按钮
    const mobileActions = [{
      key: 'more',
      label: '更多',
      icon: Bars3Icon,
      handler: () => {
        isMobileMenuOpen.value = true;
      }
    }, {
      key: 'community',
      label: '社区',
      icon: UsersIcon,
      handler: () => router.push('/community')
    }, {
      key: 'messages',
      label: '消息',
      icon: EnvelopeIcon,
      handler: () => router.push('/user?tab=messages'),
      badge: messageBadge
    }, {
      key: 'profile',
      label: '我的',
      icon: UserCircleIcon,
      handler: () => router.push('/user')
    }];

    // 修改判断当前激活的导航项逻辑
    const isActiveAction = key => {
      if (key === 'more') return false;
      if (key === 'community') return route.path.startsWith('/community');
      if (key === 'messages') return route.path.includes('/user') && route.query.tab === 'messages';
      if (key === 'profile') return route.path.includes('/user') && !route.query.tab;
      return false;
    };

    // 处理发布按钮点击
    const handlePublish = () => {
      const path = route.path;
      if (path.includes('articles')) {
        router.push('/community/articles/publish');
      } else if (path.includes('questions')) {
        router.push('/community/questions/publish');
      } else {
        router.push('/community/articles/publish');
      }
    };
    const __returned__ = {
      route,
      router,
      isMobileMenuOpen,
      unreadMessagesCount,
      messageBadge,
      menuGroups,
      mobileActions,
      isActiveAction,
      handlePublish,
      ref,
      computed,
      get useRoute() {
        return useRoute;
      },
      get useRouter() {
        return useRouter;
      },
      HeadView,
      FooterView,
      get DocumentTextIcon() {
        return DocumentTextIcon;
      },
      get QuestionMarkCircleIcon() {
        return QuestionMarkCircleIcon;
      },
      get ScaleIcon() {
        return ScaleIcon;
      },
      get UserIcon() {
        return UserIcon;
      },
      get Bars3Icon() {
        return Bars3Icon;
      },
      get XMarkIcon() {
        return XMarkIcon;
      },
      get PlusIcon() {
        return PlusIcon;
      },
      get DocumentIcon() {
        return DocumentIcon;
      },
      get EnvelopeIcon() {
        return EnvelopeIcon;
      },
      get Cog6ToothIcon() {
        return Cog6ToothIcon;
      },
      get UserCircleIcon() {
        return UserCircleIcon;
      },
      get PaintBrushIcon() {
        return PaintBrushIcon;
      },
      get VideoCameraIcon() {
        return VideoCameraIcon;
      },
      get CameraIcon() {
        return CameraIcon;
      },
      get FilmIcon() {
        return FilmIcon;
      },
      get UsersIcon() {
        return UsersIcon;
      }
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
