import { ref, computed, nextTick, watch, onMounted } from 'vue';
import { ElMessage } from 'element-plus';
import defaultAvatarImage from '@/assets/images/default-avatar.png';
import { useStore } from 'vuex';
import config from '@/config';
import { showToast } from '@/components/ToastMessage';
import { PencilIcon, PhoneIcon, EnvelopeIcon, MapPinIcon, BriefcaseIcon, ClockIcon, CurrencyYenIcon, DocumentTextIcon, CameraIcon, UserIcon, CalendarIcon, ChevronDownIcon, ChevronUpIcon, CakeIcon, PencilSquareIcon } from '@heroicons/vue/24/outline';
import profile from '@/api/profile';
import { eventBus } from '@/utils/eventBus';
import { Dialog, DialogPanel, DialogTitle, TransitionChild, TransitionRoot } from '@headlessui/vue';
import ProfileAvatarCropper from '@/components/ProfileAvatarCropper.vue';
import { useAccountStore } from '@/stores/account';
import { useProfileStore } from '@/stores/profile';

// 1. 首先声明 props

export default {
  __name: 'BasicInfo',
  props: {
    resumeData: {
      type: Object,
      default: () => ({})
    },
    loading: {
      type: Boolean,
      default: false
    },
    bioExpanded: {
      type: Boolean,
      default: false
    },
    showBioExpandButton: {
      type: Boolean,
      default: false
    }
  },
  emits: ['update', 'edit', 'toggleBioExpand'],
  setup(__props, {
    expose: __expose,
    emit: __emit
  }) {
    __expose();
    const props = __props;

    // 2. 声明 emits
    const emit = __emit;

    // 3. 声明其他必要的响应式变量
    const showAvatarCropper = ref(false);
    const avatarUploadRef = ref(null);

    // 4. 导入和初始化其他变量
    const store = useStore();
    const pageLoading = ref(true);
    const basicInfo = ref({});
    const profileStore = useProfileStore();

    // 5. 计算属性
    const formatBasicInfo = computed(() => {
      // 优先使用父组件传入的 resumeData
      const info = props.resumeData || {};
      return {
        name: info.name || info.username || '未填写',
        // 添加 username 作为备选
        gender: formatGender(info.gender),
        phone: info.phone || '未填写',
        email: info.email || '未填写',
        location: info.location || '未填写',
        age: calculateAge(info.birth_date) || '未填写',
        personal_summary: info.personal_summary || '未填写'
      };
    });

    // 添加一个 ref 来存储完整的头像 URL
    const avatarUrl = ref('');
    const avatarUpdateTime = ref(Date.now());

    // 修改计算属性
    const userAvatar = computed(() => {
      // 优先使用 store 中的头像
      const avatar = profileStore.profileAvatarUrl || props.resumeData?.avatar;
      console.log('Computing userAvatar:', {
        storeAvatar: profileStore.profileAvatarUrl,
        propsAvatar: props.resumeData?.avatar,
        finalAvatar: avatar,
        updateTime: avatarUpdateTime.value
      });
      if (!avatar) {
        avatarUrl.value = defaultAvatarImage;
        return defaultAvatarImage;
      }

      // 如果是完整的URL，直接使用
      if (avatar.startsWith('http') || avatar.startsWith('data:image')) {
        avatarUrl.value = avatar;
        return avatar;
      }

      // 处理相对路径，移除可能的前导 /media/ 并确保路径正确
      const cleanPath = avatar.replace(/^\/?(media\/)?/, '');
      const fullUrl = `${config.mediaURL}/${cleanPath}`;

      // 添加时间戳参数来避免缓存
      avatarUrl.value = `${fullUrl}?t=${avatarUpdateTime.value}`;
      return avatarUrl.value;
    });
    const currentGender = computed(() => {
      return props.resumeData?.gender;
    });

    // 6. 监听器
    watch(() => props.resumeData, newVal => {
      if (newVal && Object.keys(newVal).length > 0) {
        pageLoading.value = false;
      }
    }, {
      immediate: true
    });

    // 7. 生命周期钩子
    onMounted(async () => {
      try {
        pageLoading.value = true;
        // 不需要在这里获取数据，因为父组件会处理
      } catch (error) {
        console.error('初始化失败:', error);
      } finally {
        pageLoading.value = false;
      }
    });
    onMounted(async () => {
      try {
        await store.dispatch('fetchCompleteness');
      } catch (error) {
        console.error('Failed to fetch completeness:', error);
      }
    });

    // 8. 方法定义
    const handleFileChange = async event => {
      const file = event.target.files[0];
      if (!file) return;

      // 验证文件
      if (!validateFile(file)) {
        return;
      }
      await handleAvatarUpload(file);

      // 清空文件选择器，以便能够选择同一个文件
      event.target.value = '';
    };
    const handleEdit = () => {
      console.log('BasicInfo handleEdit - 当前数据:', props.resumeData);
      // 确保传递正确的数据结构
      const editData = {
        name: props.resumeData.name,
        gender: props.resumeData.gender,
        birth_date: props.resumeData.birth_date,
        phone: props.resumeData.phone,
        email: props.resumeData.email,
        location: props.resumeData.location,
        personal_summary: props.resumeData.personal_summary
      };
      emit('edit', 'basic_info', editData);
    };
    const toggleBioExpand = () => {
      emit('toggleBioExpand');
    };

    // 9. 工具函数
    const calculateAge = birthDate => {
      if (!birthDate) return '';
      const birth = new Date(birthDate);
      const today = new Date();
      let age = today.getFullYear() - birth.getFullYear();
      const monthDiff = today.getMonth() - birth.getMonth();
      if (monthDiff < 0 || monthDiff === 0 && today.getDate() < birth.getDate()) {
        age--;
      }
      return `${age}岁`;
    };
    const formatGender = gender => {
      const genderMap = {
        male: '男',
        female: '女',
        other: '其他'
      };
      return genderMap[gender] || '未填写';
    };
    const formatDate = dateString => {
      if (!dateString) return '';
      const date = new Date(dateString);
      return date.toLocaleDateString('zh-CN', {
        year: 'numeric',
        month: 'long',
        day: 'numeric'
      });
    };

    // 修改头像上传处理函数
    const handleAvatarUpload = async file => {
      try {
        loading.value = true;
        // 使用 profileStore 上传证件照
        await profileStore.updateProfileAvatar(file); // 注意这里改用 updateProfileAvatar 方法

        // 获取最新的完整度数据并触发更新事件
        const completenessResponse = await profile.getCompleteness();
        if (completenessResponse.data?.code === 200) {
          eventBus.emit('completeness-updated');
        }
        showToast('证件照上传成功', 'success');
      } catch (error) {
        console.error('证件照上传失败:', error);
        showToast(error.message || '证件照上传失败，请重试', 'error');
      } finally {
        loading.value = false;
      }
    };

    // 添加图片加载错误处理
    const handleImageError = e => {
      e.target.src = defaultAvatarImage;
    };

    // 添加文件验证
    const validateFile = file => {
      // 验证文件类型
      if (!file.type.startsWith('image/')) {
        showToast('请选择图片文件', 'error');
        return false;
      }

      // 验证文件大小（例如限制为 2MB）
      const maxSize = 20 * 1024 * 1024; // 2MB
      if (file.size > maxSize) {
        showToast('图片大小不能超过 2MB', 'error');
        return false;
      }
      return true;
    };

    // 添加状态
    const loading = ref(false);

    // 监听 resumeData 变化
    watch(() => props.resumeData, newVal => {
      if (newVal) {
        // 强制更新组件
        nextTick(() => {
          if (props.resumeData?.avatar) {
            const img = new Image();
            img.src = userAvatar.value;
          }
        });
      }
    }, {
      deep: true
    });
    const __returned__ = {
      props,
      emit,
      showAvatarCropper,
      avatarUploadRef,
      store,
      pageLoading,
      basicInfo,
      profileStore,
      formatBasicInfo,
      avatarUrl,
      avatarUpdateTime,
      userAvatar,
      currentGender,
      handleFileChange,
      handleEdit,
      toggleBioExpand,
      calculateAge,
      formatGender,
      formatDate,
      handleAvatarUpload,
      handleImageError,
      validateFile,
      loading,
      ref,
      computed,
      nextTick,
      watch,
      onMounted,
      get ElMessage() {
        return ElMessage;
      },
      get defaultAvatarImage() {
        return defaultAvatarImage;
      },
      get useStore() {
        return useStore;
      },
      get config() {
        return config;
      },
      get showToast() {
        return showToast;
      },
      get PencilIcon() {
        return PencilIcon;
      },
      get PhoneIcon() {
        return PhoneIcon;
      },
      get EnvelopeIcon() {
        return EnvelopeIcon;
      },
      get MapPinIcon() {
        return MapPinIcon;
      },
      get BriefcaseIcon() {
        return BriefcaseIcon;
      },
      get ClockIcon() {
        return ClockIcon;
      },
      get CurrencyYenIcon() {
        return CurrencyYenIcon;
      },
      get DocumentTextIcon() {
        return DocumentTextIcon;
      },
      get CameraIcon() {
        return CameraIcon;
      },
      get UserIcon() {
        return UserIcon;
      },
      get CalendarIcon() {
        return CalendarIcon;
      },
      get ChevronDownIcon() {
        return ChevronDownIcon;
      },
      get ChevronUpIcon() {
        return ChevronUpIcon;
      },
      get CakeIcon() {
        return CakeIcon;
      },
      get PencilSquareIcon() {
        return PencilSquareIcon;
      },
      get profile() {
        return profile;
      },
      get eventBus() {
        return eventBus;
      },
      get Dialog() {
        return Dialog;
      },
      get DialogPanel() {
        return DialogPanel;
      },
      get DialogTitle() {
        return DialogTitle;
      },
      get TransitionChild() {
        return TransitionChild;
      },
      get TransitionRoot() {
        return TransitionRoot;
      },
      ProfileAvatarCropper,
      get useAccountStore() {
        return useAccountStore;
      },
      get useProfileStore() {
        return useProfileStore;
      }
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
