import { defineStore } from 'pinia'
import { user } from '@/api/user'
import defaultAvatar from '@/assets/images/default-avatar.png'

export const useAccountStore = defineStore('account', {
  state: () => ({
    userInfo: JSON.parse(localStorage.getItem('userInfo')) || null,
    avatarUpdateTime: null,
    backgroundImage: null
  }),

  getters: {
    avatar: (state) => state.userInfo?.avatar || defaultAvatar,
    username: (state) => state.userInfo?.username || '未设置用户名',
    uid: (state) => state.userInfo?.uid,
    background: (state) => state.backgroundImage,
    phone: (state) => state.userInfo?.phone,
    email: (state) => state.userInfo?.email,
    position: (state) => state.userInfo?.position,
    bio: (state) => state.userInfo?.bio,
    isVip: (state) => state.userInfo?.is_vip || false,
    isStaff: (state) => state.userInfo?.is_staff || false
  },

  actions: {
    async fetchUserInfo() {
      try {
        const response = await user.getUserInfo()
        if (response?.data?.code === 200) {
          const userData = response.data.data

          this.userInfo = {
            id: userData.id,
            uid: userData.uid,
            username: userData.username,
            phone: userData.phone || null,
            email: userData.email || null,
            avatar: userData.avatar || null,
            position: userData.position || null,
            bio: userData.bio || null,
            is_vip: userData.is_vip || false,
            is_staff: userData.is_staff || false
          }

          this.backgroundImage = userData.background_image || null

          console.log('Storing user info:', this.userInfo)

          localStorage.setItem('userInfo', JSON.stringify(this.userInfo))
          localStorage.setItem('backgroundImage', this.backgroundImage)

          return this.userInfo
        }
        return null
      } catch (error) {
        console.error('获取用户信息失败:', error)
        throw error
      }
    },

    async updateAvatar(formData) {
      try {
        const file = formData.get('avatar')
        const response = await user.updateAvatar(file)
        if (response?.data?.code === 200) {
          this.avatarUpdateTime = Date.now()
          this.userInfo = {
            ...this.userInfo,
            avatar: response.data.data.avatar
          }
          localStorage.setItem('userInfo', JSON.stringify(this.userInfo))
          return response
        }
        throw new Error(response?.data?.message || '更新头像失败')
      } catch (error) {
        console.error('Failed to update avatar:', error)
        throw error
      }
    },

    async updateBackground(formData) {
      try {
        const file = formData.get('background')
        const response = await user.updateBackground(file)
        if (response?.data?.code === 200) {
          this.backgroundImage = response.data.data.background || null
          localStorage.setItem('userInfo', JSON.stringify(this.userInfo))
          return response
        }
        throw new Error(response?.data?.message || '更新背景图失败')
      } catch (error) {
        console.error('Failed to update background:', error)
        throw error
      }
    },

    async updateUserInfo(data) {
      try {
        console.log('Updating user info with:', data)
        const response = await user.updateUserInfo(data)
        console.log('Update response:', response.data)
        if (response?.data?.code === 200) {
          const userData = response.data.data

          this.userInfo = {
            ...this.userInfo,
            id: userData.id,
            uid: userData.uid,
            username: userData.username,
            phone: userData.phone || null,
            email: userData.email || null,
            avatar: userData.avatar || null,
            position: userData.position || null,
            bio: userData.bio || null,
            is_vip: userData.is_vip || false,
            is_staff: userData.is_staff || false
          }

          localStorage.setItem('userInfo', JSON.stringify(this.userInfo))

          if (userData.username !== data.username) {
            showToast(`用户名已被修改为 ${userData.username}`, 'warning')
          }

          console.log('Updated user info:', this.userInfo)
          return response
        }
        throw new Error(response?.data?.message || '更新用户信息失败')
      } catch (error) {
        console.error('Failed to update user info:', error)
        if (error.response) {
          console.error('Error response:', error.response.data)
        }
        throw error
      }
    },

    clearUserInfo() {
      this.userInfo = null
      this.avatarUpdateTime = null
      this.backgroundImage = null
      localStorage.removeItem('userInfo')
      localStorage.removeItem('avatarUpdateTime')
      localStorage.removeItem('backgroundImage')
    }
  }
})
