        // 如果没有成功状态码也没有成功消息，才抛出错误
        throw new Error(response?.data?.message || '邮箱更新失败');
      } catch (error) {
        console.error('邮箱更新失败:', error);
        // 如果错误消息包含"成功"，说明实际上是成功的
        if (error.message?.includes('成功')) {
          closeEmailModal();
          await userStore.getUserInfo();
          showToast(error.message, 'success');
          return;
        }

        // 真正的错误处理
        const errorMsg = error.response?.data?.detail || error.response?.data?.message || error.message || '邮箱操作失败';
        showToast(errorMsg, 'error');
      }
