    // 添加计算属性控制邮箱表单按钮状态
    const isEmailFormValid = computed(() => {
      // 检查邮箱格式
      const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
      const isValidEmail = emailRegex.test(emailManager.state.value);

      // 检查所有必填字段
      return emailManager.state.value && emailManager.state.code && emailManager.state.password &&
      // 密码必填
      isValidEmail && !emailManager.state.loading;
