    // 添加防重复点击状态
    const isRequestingCode = ref(false);

    // 处理邮箱更新的验证码发送
    const handleEmailSendCode = async () => {
      if (!emailManager.state.value) {
        showToast('请输入新邮箱', 'warning');
        return;
      }
