import {
  HomeIcon,
  DevicePhoneMobileIcon,
  SwatchIcon,
  GlobeAltIcon,
  FilmIcon,
  VideoCameraIcon,
  AcademicCapIcon,
  Square2StackIcon,
  CubeIcon,
  SparklesIcon,
  UserGroupIcon,
  CameraIcon,
  PhotoIcon
} from '@heroicons/vue/24/outline'

export const portfolioCategories = {
  design: {
    title: '设计作品',
    description: '展示优秀的设计作品，分享设计灵感',
    categories: [
      { id: 'all', name: '全部', icon: HomeIcon },
      {
        id: 'ui',
        name: 'UI设计',
        icon: DevicePhoneMobileIcon,
        children: [
          { id: 'mobile', name: '移动端' },
          { id: 'web', name: '网页端' },
          { id: 'desktop', name: '桌面端' }
        ]
      },
      {
        id: 'graphic',
        name: '平面设计',
        icon: SwatchIcon,
        children: [
          { id: 'poster', name: '海报' },
          { id: 'brand', name: '品牌' },
          { id: 'packaging', name: '包装' }
        ]
      },
      {
        id: 'web',
        name: 'Web设计',
        icon: GlobeAltIcon,
        children: [
          { id: 'website', name: '网站设计' },
          { id: 'landing', name: '落地页' },
          { id: 'admin', name: '后台系统' }
        ]
      }
    ]
  },
  video: {
    title: '视频作品',
    description: '分享创意视频作品，展现独特视角',
    categories: [
      { id: 'all', name: '全部', icon: HomeIcon },
      {
        id: 'commercial',
        name: '商业广告',
        icon: FilmIcon,
        children: [
          { id: 'product', name: '产品广告' },
          { id: 'brand', name: '品牌广告' },
          { id: 'service', name: '服务广告' }
        ]
      },
      // ... 其他视频分类
    ]
  },
  // ... 动画和摄影的分类配置
}

// 生成菜单组配置的辅助函数
export function generateMenuGroups(type) {
  const config = portfolioCategories[type]
  return [
    {
      title: `${config.title}分类`,
      items: config.categories.map(category => ({
        name: category.name,
        path: `/portfolio/${type}?category=${category.id}`,
        icon: category.icon
      }))
    }
  ]
}
