import request from '@/utils/request'

export const user = {
  // 获取用户信息
  getUserInfo: () => {
    return request.get('/api/v1/users/userInfo/')
  },

  // 更新用户信息
  updateUserInfo: (data) => {
    const formData = new FormData()
    if (data.avatar) formData.append('avatar', data.avatar)
    if (data.background_image) formData.append('background_image', data.background_image)
    if (data.position) formData.append('position', data.position)
    if (data.bio) formData.append('bio', data.bio)
    if (data.username) formData.append('username', data.username)
    return request.put('/api/v1/users/userInfo/', formData, {
      headers: {
        'Content-Type': 'multipart/form-data'
      }
    })
  },


  // 更换手机号
  changePhone: (data) => {
    return request.post('/api/v1/users/account/phone/', {
      phone: data.phone,
      code: data.code
    })
  },

  // 修改密码
  updatePassword: (data) => {
    return request.post('/api/v1/users/account/password/', {
      old_password: data.old_password,
      new_password: data.new_password,
      confirm_password: data.confirm_password
    })
  },

  // 上传背景图
  uploadBackground: (file) => {
    const formData = new FormData()
    formData.append('background', file)
    return request.put('/api/v1/users/userInfo/', formData, {
      headers: {
        'Content-Type': 'multipart/form-data'
      }
    })
  },
}

export const updateNickname = (nickname) => {
  return user.updateUsername({ username: nickname })
}

export default user
