const config = {
  // 开发环境配置
  development: {
    API_URL: 'http://192.168.3.16:8000'
  },
  // 生产环境配置
  production: {
    API_URL: 'http://api.example.com'  // 生产环境 API 地址
  },
  // 测试环境配置
  test: {
    API_URL: 'http://test-api.example.com'
  }
}

// 获取当前环境
const env = process.env.NODE_ENV || 'development'

// 导出当前环境的配置
export default config[env]
