import request from '@/utils/request'

export const user = {
  // 获取用户信息
  getUserInfo: () => {
    return request.get('/api/v1/users/userInfo/')
  },

  // 更新用户头像
  updateAvatar: (data) => {
    const formData = new FormData()
    formData.append('avatar', data)
    return request.put('/api/v1/users/userInfo/', formData, {
      headers: {
        'Content-Type': 'multipart/form-data'
      }
    })
  },

  // 更新用户背景图
  updateBackground: (data) => {
    const formData = new FormData()
    formData.append('background_image', data)
    return request.put('/api/v1/users/userInfo/', formData, {
      headers: {
        'Content-Type': 'multipart/form-data'
      }
    })
  },

  // 更新用户基本信息
  updateUserInfo: (data) => {
    return request.put('/api/v1/users/userInfo/', data)
  },

  // 获取用户资料完整度
  getProfileCompleteness: () => {
    return request.get('/api/v1/users/account/completeness/')
  }
}

export const updateNickname = (nickname) => {
  return user.updateUsername({ username: nickname })
}

export default user
