import { ref, onMounted, computed, watch, nextTick, onUnmounted } from 'vue';
import { useRouter, useRoute } from 'vue-router';
import { ElMessage } from 'element-plus';
import { useAccountStore } from '@/stores/account';
import config from '@/config';
import defaultAvatar from '@/assets/images/default-avatar.png';
import defaultBackground from '@/assets/images/default-background.jpg';
import { showToast } from '@/components/ToastMessage';
import { CameraIcon,
// 用于头像和背景上传
PhotoIcon,
// 作品集图标
PencilSquareIcon,
// 编辑图标
TrophyIcon,
// 会员图标
SparklesIcon,
// 升级会员图标
PencilIcon,
// 编辑昵称图标
ChevronRightIcon,
// 箭头图标
PlusIcon,
// 添加图标
TrashIcon,
// 删除图标
ChatBubbleLeftRightIcon,
// 话题图标
QuestionMarkCircleIcon,
// 问答图标
InboxArrowDownIcon,
// 草稿箱图标
EyeIcon,
// 查看图标
BookmarkIcon,
// 收藏图标
ArrowTopRightOnSquareIcon,
// 外链图标
GlobeAltIcon,
// 个人主页图标
XMarkIcon // 关闭图标
} from '@heroicons/vue/24/outline';
import { user } from '@/api/user';
import { eventBus } from '@/utils/eventBus';
export default {
  __name: 'UserHome',
  setup(__props, {
    expose: __expose
  }) {
    __expose();
    const router = useRouter();
    const route = useRoute();
    const accountStore = useAccountStore();
    const showEditModal = ref(false);
    const editForm = ref({
      username: '',
      position: '',
      bio: '',
      avatar: null
    });
    const avatarInput = ref(null);
    const usernameError = ref('');
    const creationSection = ref(null);

    // 使用 getters 获取用户信息
    const userInfo = computed(() => ({
      uid: accountStore.uid,
      username: accountStore.username,
      avatar: accountStore.avatar,
      background: accountStore.background,
      bio: accountStore.bio || null,
      position: accountStore.position || null
    }));

    // 格式化图片 URL
    const formatImageUrl = url => {
      if (!url || url === 'null') return defaultAvatarImage;
      return url.startsWith('http') ? url : `${config.MEDIA_URL}${url}`;
    };

    // 计算头像 URL
    const userAvatar = computed(() => {
      if (!userInfo.value?.avatar) return defaultAvatar;
      if (userInfo.value.avatar.startsWith('http') || userInfo.value.avatar.startsWith('data:image')) {
        return userInfo.value.avatar;
      }
      return userInfo.value.avatar.startsWith('/') ? `${config.MEDIA_URL}${userInfo.value.avatar}` : `${config.MEDIA_URL}/${userInfo.value.avatar}`;
    });

    // 编辑头像 URL 的计算属性
    const editAvatarUrl = computed(() => {
      const avatarUrl = editForm.value.avatar || userInfo.value?.avatar;
      if (!avatarUrl) return defaultAvatarImage;
      return avatarUrl.startsWith('http') ? avatarUrl : `${config.MEDIA_URL}${avatarUrl}`;
    });

    // 添加背景图 URL 的计算属性
    const backgroundUrl = computed(() => {
      if (!userInfo.value?.background) return defaultBackground;
      if (userInfo.value.background.startsWith('http') || userInfo.value.background.startsWith('data:image')) {
        return userInfo.value.background;
      }
      return userInfo.value.background.startsWith('/') ? `${config.MEDIA_URL}${userInfo.value.background}` : `${config.MEDIA_URL}/${userInfo.value.background}`;
    });

    // 获取用户信息
    const fetchUserInfo = async () => {
      try {
        // 只在 store 中没有数据时获取
        if (!accountStore.userInfo) {
          await accountStore.fetchUserInfo();
        }
      } catch (error) {
        console.error('获取用户信息失败:', error);
      }
    };

    // 滚动到创作区域
    const scrollToCreation = () => {
      if (creationSection.value) {
        const offset = 100; // 可以根据需要调整这个值
        const elementPosition = creationSection.value.getBoundingClientRect().top;
        const offsetPosition = elementPosition + window.pageYOffset - offset;
        window.scrollTo({
          top: offsetPosition,
          behavior: 'smooth'
        });
      }
    };

    // 监听路由参数变化
    watch(() => route.query.currentTab, newTab => {
      if (newTab === 'articles' || newTab === 'drafts') {
        // 等待 DOM 更新后滚动
        nextTick(() => {
          scrollToCreation();
        });
      }
    }, {
      immediate: true
    });

    // 组件挂载时获取数据
    onMounted(async () => {
      // 只在 store 中没有数据时获取
      if (!accountStore.userInfo) {
        await fetchUserInfo();
      }

      // 如果当前是文章或草稿标签，滚动到创作区域
      if (route.query.currentTab === 'articles' || route.query.currentTab === 'drafts') {
        scrollToCreation();
      }
    });

    // 头像上传相关
    const showAvatarUpload = ref(false);
    const loading = ref(false);

    // 处理头像错误
    const handleImageError = e => {
      e.target.src = defaultAvatarImage;
    };

    // 处理背景图错误
    const handleBackgroundError = e => {
      e.target.src = defaultBackground;
    };

    // 处理个人资料更新
    const handleProfileUpdate = async () => {
      if (!validateUsername()) return;
      try {
        loading.value = true;
        // 准备更新数据
        const updateData = {
          username: editForm.value.username.trim(),
          position: editForm.value.position?.trim() || '',
          bio: editForm.value.bio?.trim() || '',
          phone: accountStore.phone,
          email: accountStore.email
        };
        console.log('Submitting update:', updateData);
        const response = await accountStore.updateUserInfo(updateData);

        // 使用后端返回的数据更新表单
        if (response?.data?.code === 200) {
          const userData = response.data.data;
          editForm.value = {
            username: userData.username,
            position: userData.position || '',
            bio: userData.bio || '',
            avatar: userData.avatar
          };
        }
        showToast('更新成功', 'success');
        closeEditModal();
      } catch (error) {
        console.error('更新失败:', error);
        showToast(error.message || '更新失败，请稍后重试', 'error');
      } finally {
        loading.value = false;
      }
    };

    // 实时验证用户名
    const validateUsername = () => {
      const value = editForm.value.username;
      if (!value) {
        usernameError.value = '用户名不能为空';
      } else if (value.length < 4 || value.length > 11) {
        usernameError.value = '用户名长度必须在4-11个字符之间';
      } else if (!/^[\u4e00-\u9fa5a-zA-Z0-9]+$/.test(value)) {
        usernameError.value = '用户名只能包含中文、英文字母和数字';
      } else {
        usernameError.value = '';
      }
      return !usernameError.value;
    };

    // 打开编辑弹窗时初始化表单数据
    const openEditModal = () => {
      editForm.value = {
        username: userInfo.value.username || '',
        position: userInfo.value.position || '',
        bio: userInfo.value.bio || '',
        avatar: userInfo.value.avatar || null
      };
      showEditModal.value = true;
    };

    // 关闭编辑弹窗
    const closeEditModal = () => {
      showEditModal.value = false;
      editForm.value = {
        username: '',
        position: '',
        bio: '',
        avatar: null
      };
      usernameError.value = '';
    };
    const handleAvatarChange = async event => {
      const file = event.target.files[0];
      if (!file) return;

      // 验证文件
      if (!file.type.startsWith('image/')) {
        showToast('请选择图片文件', 'error');
        return;
      }
      const maxSize = 2 * 1024 * 1024; // 2MB
      if (file.size > maxSize) {
        showToast('图片大小不能超过2MB', 'error');
        return;
      }
      try {
        loading.value = true;

        // 直接使用 user API 上传头像
        const response = await user.updateAvatar(file);
        if (response.data.code === 200) {
          const newAvatar = response.data.data.avatar;
          // 更新 accountStore 中的头像
          await accountStore.fetchUserInfo();
          // 更新编辑表单中的头像
          editForm.value.avatar = newAvatar;
          showToast('头像上传成功', 'success');

          // 触发全局事件，通知其他组件（如 HeadView）更新头像
          eventBus.emit('avatar-updated', newAvatar);
        }
      } catch (error) {
        console.error('头像上传失败:', error);
        showToast(error.message || '头像上传失败，请稍后重试', 'error');
      } finally {
        loading.value = false;
        // 清空文件选择器
        event.target.value = '';
      }
    };

    // 格式化存储空间大小
    const formatStorage = bytes => {
      if (!bytes) return '0 B';
      const k = 1024;
      const sizes = ['B', 'KB', 'MB', 'GB', 'TB'];
      const i = Math.floor(Math.log(bytes) / Math.log(k));
      return `${parseFloat((bytes / Math.pow(k, i)).toFixed(2))} ${sizes[i]}`;
    };

    // 存储空间使用数据
    const storageUsed = ref(0); // 字节数
    const storageUsedPercent = computed(() => {
      const maxStorage = 10 * 1024 * 1024 * 1024; // 10GB in bytes
      return Math.round(storageUsed.value / maxStorage * 100);
    });

    // 统计数据
    const statistics = ref([{
      label: '关注',
      value: '0'
    }, {
      label: '粉丝',
      value: '0'
    }, {
      label: '点赞',
      value: '0'
    }, {
      label: '访问',
      value: '0'
    }]);

    // 内容相关数据
    const currentTab = ref(route.query.currentTab || 'articles');
    const articles = ref([]);
    const portfolios = ref([]);

    // 收藏相关数据
    const currentFavTab = ref('portfolios');
    const favoritesTabs = [{
      key: 'portfolios',
      label: '作品集'
    }, {
      key: 'articles',
      label: '文章'
    }, {
      key: 'topics',
      label: '话题'
    }];

    // 内容标签
    const contentTabs = [{
      key: 'articles',
      label: '文章',
      count: 0
    }, {
      key: 'topics',
      label: '话题',
      count: 0
    }, {
      key: 'qa',
      label: '问答',
      count: 0
    }, {
      key: 'drafts',
      label: '草稿箱',
      count: 0
    }];

    // 获取当前收藏标签的标签文本
    const getCurrentFavTabLabel = () => {
      const tab = favoritesTabs.find(tab => tab.key === currentFavTab.value);
      return tab ? tab.label : '';
    };

    // 获取收藏探索路由
    const getFavoritesExploreRoute = () => {
      const routes = {
        portfolios: '/explore/portfolios',
        articles: '/explore/articles',
        topics: '/explore/topics'
      };
      return routes[currentFavTab.value] || '/explore';
    };

    // 处理图片错误
    const handlePortfolioImageError = e => {
      e.target.src = defaultAvatarImage;
    };
    const handleArticleImageError = e => {
      e.target.src = defaultAvatarImage;
    };

    // 格式化日期
    const formatDate = date => {
      if (!date) return '';
      return new Date(date).toLocaleDateString('zh-CN', {
        year: 'numeric',
        month: 'long',
        day: 'numeric'
      });
    };

    // 处理背景图上传
    const handleBackgroundUpload = async () => {
      const input = document.createElement('input');
      input.type = 'file';
      input.accept = 'image/*';
      input.onchange = async e => {
        const file = e.target.files[0];
        if (!file) return;
        try {
          loading.value = true;
          const formData = new FormData();
          formData.append('background', file);
          await accountStore.updateBackground(formData);
          showToast('背景图上传成功', 'success');
        } catch (error) {
          console.error('背景图上传失败:', error);
          showToast(error.message || '背景图上传失败，请稍后重试', 'error');
        } finally {
          loading.value = false;
          input.value = '';
        }
      };
      input.click();
    };

    // 用户主页链接
    const userProfileUrl = computed(() => {
      return `/u/${userInfo.value.uid}`;
    });

    // 监听路由参数变化，更新当前标签页
    watch(() => route.query.currentTab, newTab => {
      if (newTab) {
        currentTab.value = newTab;
      }
    });

    // 处理头像选择
    const handleAvatarSelect = () => {
      avatarInput.value?.click();
    };

    // 监听头像更新事件
    onMounted(() => {
      eventBus.on('avatar-updated', async () => {
        await accountStore.fetchUserInfo();
      });
    });
    onUnmounted(() => {
      eventBus.off('avatar-updated');
    });
    const __returned__ = {
      router,
      route,
      accountStore,
      showEditModal,
      editForm,
      avatarInput,
      usernameError,
      creationSection,
      userInfo,
      formatImageUrl,
      userAvatar,
      editAvatarUrl,
      backgroundUrl,
      fetchUserInfo,
      scrollToCreation,
      showAvatarUpload,
      loading,
      handleImageError,
      handleBackgroundError,
      handleProfileUpdate,
      validateUsername,
      openEditModal,
      closeEditModal,
      handleAvatarChange,
      formatStorage,
      storageUsed,
      storageUsedPercent,
      statistics,
      currentTab,
      articles,
      portfolios,
      currentFavTab,
      favoritesTabs,
      contentTabs,
      getCurrentFavTabLabel,
      getFavoritesExploreRoute,
      handlePortfolioImageError,
      handleArticleImageError,
      formatDate,
      handleBackgroundUpload,
      userProfileUrl,
      handleAvatarSelect,
      ref,
      onMounted,
      computed,
      watch,
      nextTick,
      onUnmounted,
      get useRouter() {
        return useRouter;
      },
      get useRoute() {
        return useRoute;
      },
      get ElMessage() {
        return ElMessage;
      },
      get useAccountStore() {
        return useAccountStore;
      },
      get config() {
        return config;
      },
      get defaultAvatar() {
        return defaultAvatar;
      },
      get defaultBackground() {
        return defaultBackground;
      },
      get showToast() {
        return showToast;
      },
      get CameraIcon() {
        return CameraIcon;
      },
      get PhotoIcon() {
        return PhotoIcon;
      },
      get PencilSquareIcon() {
        return PencilSquareIcon;
      },
      get TrophyIcon() {
        return TrophyIcon;
      },
      get SparklesIcon() {
        return SparklesIcon;
      },
      get PencilIcon() {
        return PencilIcon;
      },
      get ChevronRightIcon() {
        return ChevronRightIcon;
      },
      get PlusIcon() {
        return PlusIcon;
      },
      get TrashIcon() {
        return TrashIcon;
      },
      get ChatBubbleLeftRightIcon() {
        return ChatBubbleLeftRightIcon;
      },
      get QuestionMarkCircleIcon() {
        return QuestionMarkCircleIcon;
      },
      get InboxArrowDownIcon() {
        return InboxArrowDownIcon;
      },
      get EyeIcon() {
        return EyeIcon;
      },
      get BookmarkIcon() {
        return BookmarkIcon;
      },
      get ArrowTopRightOnSquareIcon() {
        return ArrowTopRightOnSquareIcon;
      },
      get GlobeAltIcon() {
        return GlobeAltIcon;
      },
      get XMarkIcon() {
        return XMarkIcon;
      },
      get user() {
        return user;
      },
      get eventBus() {
        return eventBus;
      }
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
