import request from '@/utils/request'

export default {
  // 获取模板列表
  getTemplates() {
    return request.get('/api/v1/resume/templates/')
  },

  // 获取模板详情
  getTemplateDetail(id) {
    return request.get(`/api/v1/resume/templates/${id}/`)
  },

  // 创建模板
  createTemplate(data) {
    return request.post('/api/v1/resume/templates/', data)
  },

  // 更新模板
  updateTemplate(id, data) {
    return request.put(`/api/v1/resume/templates/${id}/`, data)
  },

  // 删除模板
  deleteTemplate(id) {
    return request.delete(`/api/v1/resume/templates/${id}/`)
  }
}
