import { defineStore } from 'pinia'
import { auth } from '@/api/auth'
import request from '@/utils/request'
import router from '@/router'
import { useAccountStore } from '@/stores/account'
import { useUserStore } from '@/stores/user'

export const useAuthStore = defineStore('auth', {
  state: () => ({
    isLoggedIn: false,
    rememberMe: false,
    token: null,
    refreshToken: null,
    tokenExpiresAt: null
  }),

  actions: {
    async login(credentials, rememberMe = false) {
      try {
        const response = await auth.loginWithPassword(credentials)
        if (response) {
          // 保存 token
          this.token = response.token
          this.refreshToken = response.refresh
          localStorage.setItem('token', response.token)
          localStorage.setItem('refresh_token', response.refresh)

          // 设置请求头
          request.defaults.headers.common['Authorization'] = `Bearer ${response.token}`

          // 设置登录状态
          this.isLoggedIn = true

          // 登录成功后立即获取用户信息并存储
          const accountStore = useAccountStore()
          await accountStore.fetchUserInfo()

          // 记住账号功能
          if (rememberMe) {
            localStorage.setItem('remember_me', 'true')
            localStorage.setItem('remembered_account', credentials.account)
          }

          // 显示登录成功提示
          showToast('登录成功', 'success')

          return true
        }
        return false
      } catch (error) {
        console.error('Login failed:', error)
        showToast(error.message || '登录失败，请稍后重试', 'error')
        throw error
      }
    },

    async logout() {
      try {
        if (this.refreshToken) {
          await auth.logout()
        }
      } finally {
        this.clearAuth()
        const accountStore = useAccountStore()
        const userStore = useUserStore()
        accountStore.clearUserInfo()
        userStore.clearUserInfo()

        // 清除所有相关的 localStorage 数据
        const keysToRemove = [
          'token',
          'refresh_token',
          'token_expires_at',
          'remember_me',
          'remembered_account',
          'userInfo',
          'isLoggedIn',
          'avatarUpdateTime',
          'backgroundImage',
          // 添加其他可能存在的 key
        ]

        keysToRemove.forEach(key => {
          localStorage.removeItem(key)
        })

        router.push('/login')
      }
    },

    clearAuth() {
      // 清除请求头
      delete request.defaults.headers.common['Authorization']

      // 重置状态
      this.isLoggedIn = false
      this.token = null
      this.refreshToken = null
      this.tokenExpiresAt = null
      this.rememberMe = false
    },

    // 检查 token 是否过期
    isTokenExpired() {
      return !this.tokenExpiresAt || new Date().getTime() > this.tokenExpiresAt
    },

    // 从 localStorage 恢复认证状态
    restoreAuth() {
      const token = localStorage.getItem('token')
      const refreshToken = localStorage.getItem('refresh_token')
      const tokenExpiresAt = localStorage.getItem('token_expires_at')
      const rememberMe = localStorage.getItem('remember_me') === 'true'

      if (token && refreshToken && tokenExpiresAt) {
        this.token = token
        this.refreshToken = refreshToken
        this.tokenExpiresAt = parseInt(tokenExpiresAt)
        this.rememberMe = rememberMe
        this.isLoggedIn = !this.isTokenExpired()

        if (this.isLoggedIn) {
          request.defaults.headers.common['Authorization'] = `Bearer ${token}`
        }
      }
    }
  }
})
