import { defineStore } from 'pinia'
import profile from '@/api/profile'
import { eventBus } from '@/utils/eventBus'

export const useProfileStore = defineStore({
  id: 'profile',
  state: () => ({
    basicInfo: null,
    avatarUpdateTime: null
  }),

  getters: {
    profileAvatar: (state) => {
      const url = state.basicInfo?.avatar
      return url ? `${import.meta.env.VITE_API_URL}${url}` : null
    }
  },

  actions: {
    // 清理 store 数据
    clearStore() {
      this.basicInfo = null
      this.avatarUpdateTime = null
      localStorage.removeItem('profile')  // 清除持久化数据
    },

    // 获取档案基本信息
    async fetchBasicInfo() {
      try {
        const response = await profile.getData()
        if (response?.data?.code === 200 && response?.data?.data?.basic_info) {
          console.log('Fetched basicInfo:', response.data.data.basic_info)
          this.basicInfo = response.data.data.basic_info
          this.avatarUpdateTime = Date.now()
          console.log('Store state after fetch:', {
            basicInfo: this.basicInfo,
            avatarUpdateTime: this.avatarUpdateTime
          })
          return this.basicInfo
        }
        return null
      } catch (error) {
        console.error('获取档案基本信息失败:', error)
        throw error
      }
    },

    // 更新头像
    async updateAvatar(file) {
      try {
        const response = await profile.uploadAvatar(file)
        if (response?.data?.code === 200) {
          const avatarUrl = response.data.data.avatar
          console.log('Profile store - 更新头像:', avatarUrl)

          // 如果 basicInfo 为空，先初始化
          if (!this.basicInfo) {
            this.basicInfo = {}
          }

          // 更新 store 状态
          this.basicInfo.avatar = avatarUrl
          this.avatarUpdateTime = Date.now()

          console.log('Profile store - 状态已更新:', {
            avatar: this.basicInfo.avatar,
            updateTime: this.avatarUpdateTime
          })

          // 重新获取完整的档案数据
          await this.fetchBasicInfo()

          return response
        }
        throw new Error(response?.data?.message || '上传失败')
      } catch (error) {
        console.error('Profile avatar update error:', error)
        throw error
      }
    }
  },

  persist: {
    enabled: true,
    strategies: [
      {
        key: 'profile',
        storage: localStorage,
        paths: ['basicInfo', 'avatarUpdateTime']
      }
    ]
  }
})
