export default {
  __name: 'CampusView',
  setup(__props, {
    expose: __expose
  }) {
    __expose();
    const currentCategory = ref('all');

    // 模板数据
    const templates = ref([{
      id: 1,
      title: '应届生求职简历',
      description: '突出学历背景和实习经验，帮助应届生展现职场竞争力。',
