      },
      views: 2341,
      likes: 178,
      type: 'graduate'
    }
    // ... 更多模板数据
    ]);

    // 筛选逻辑
    const filteredTemplates = computed(() => {
      if (currentCategory.value === 'all') {
        return templates.value;
      }
      return templates.value.filter(template => template.type === currentCategory.value);
    });
    const __returned__ = {
      currentCategory,
      templates,
      filteredTemplates,
      TemplateNavigation,
      TemplateCard,
      ref,
      computed
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
