import request from '@/utils/request'

export const account = {
  // 更新密码
  updatePassword: (data) => {
    return request({
      url: '/api/v1/users/account/password/',
      method: 'post',
      data: {
        old_password: data.old_password,
        new_password: data.new_password,
        confirm_password: data.confirm_password
      }
    })
  },

  // 更换手机号
  changePhone: (data) => {
    return request.post('/api/v1/users/account/phone/', {
      phone: data.new_phone,
      code: data.code
    })
  },

  // 更换邮箱
  updateEmail: (data) => {
    return request.post('/api/v1/users/account/email/', data)
  },

  // 注销账号
  deleteAccount: (data) => {
    return request({
      url: '/api/v1/users/account/delete/',
      method: 'POST',
      data
    })
  },

  // 发送验证码 (用于更换手机号和邮箱)
  sendVerifyCode: (data) => {
    return request({
      url: '/api/v1/users/auth/sms/send/',
      method: 'post',
      data: {
        phone: data.phone,
        scene: data.scene,
        type: 'sms'
      }
    })
  }
}

export default account
