import { ref, computed, nextTick, watch, onMounted } from 'vue';
import { ElMessage } from 'element-plus';
import defaultAvatarImage from '@/assets/images/default-avatar.png';
import { useStore } from 'vuex';
import { MEDIA_URL } from '@/config';
import { showToast } from '@/components/ToastMessage';
import { PencilIcon, PhoneIcon, EnvelopeIcon, MapPinIcon, BriefcaseIcon, ClockIcon, CurrencyYenIcon, DocumentTextIcon, CameraIcon, UserIcon, CalendarIcon, ChevronDownIcon, ChevronUpIcon, CakeIcon, PencilSquareIcon } from '@heroicons/vue/24/outline';
import profile from '@/api/profile';
import { eventBus } from '@/utils/eventBus';
import { Dialog, DialogPanel, DialogTitle, TransitionChild, TransitionRoot } from '@headlessui/vue';
import ProfileAvatarUpload from '@/components/ProfileAvatarUpload.vue';
import { useAccountStore } from '@/stores/account';

// 1. 首先声明 props

export default {
  __name: 'BasicInfo',
  props: {
    resumeData: {
      type: Object,
      default: () => ({})
    },
    loading: {
      type: Boolean,
      default: false
    },
    bioExpanded: {
      type: Boolean,
      default: false
    },
    showBioExpandButton: {
      type: Boolean,
      default: false
    }
  },
  emits: ['update', 'edit', 'toggleBioExpand'],
  setup(__props, {
    expose: __expose,
    emit: __emit
  }) {
    __expose();
    const props = __props;

    // 2. 声明 emits
    const emit = __emit;

    // 3. 导入和初始化其他变量
    const store = useStore();
    const pageLoading = ref(true);
    const basicInfo = ref({});

    // 4. 计算属性
    const formatBasicInfo = computed(() => {
      // 优先使用父组件传入的 resumeData
      const info = props.resumeData || {};
      return {
        name: info.name || info.username || '未填写',
        // 添加 username 作为备选
        gender: formatGender(info.gender),
        phone: info.phone || '未填写',
        email: info.email || '未填写',
        location: info.location || '未填写',
        age: calculateAge(info.birth_date) || '未填写',
        personal_summary: info.personal_summary || '未填写'
      };
    });
    const userAvatar = computed(() => {
      const avatar = props.resumeData?.avatar;
      if (!avatar) return defaultAvatarImage;
      return avatar.startsWith('http') || avatar.startsWith('data:') ? avatar : `${MEDIA_URL}${avatar}`;
    });
    const currentGender = computed(() => {
      return props.resumeData?.gender;
    });

    // 5. 监听器
    watch(() => props.resumeData, newVal => {
      if (newVal && Object.keys(newVal).length > 0) {
        pageLoading.value = false;
      }
    }, {
      immediate: true
    });

    // 6. 生命周期钩子
    onMounted(async () => {
      try {
        pageLoading.value = true;
        // 不需要在这里获取数据，因为父组件会处理
      } catch (error) {
        console.error('初始化失败:', error);
      } finally {
        pageLoading.value = false;
      }
    });
    onMounted(async () => {
      try {
        await store.dispatch('fetchCompleteness');
      } catch (error) {
        console.error('Failed to fetch completeness:', error);
      }
    });

    // 7. 方法定义
    const handleFileChange = async event => {
      const file = event.target.files[0];
      if (!file) return;

      // 验证文件
      if (!validateFile(file)) {
        return;
      }
      await handleAvatarUpload(file);

      // 清空文件选择器，以便能够选择同一个文件
      event.target.value = '';
    };
    const handleEdit = () => {
      console.log('BasicInfo handleEdit - 当前数据:', props.resumeData);
      // 确保传递正确的数据结构
      const editData = {
        name: props.resumeData.name,
        gender: props.resumeData.gender,
        birth_date: props.resumeData.birth_date,
        phone: props.resumeData.phone,
        email: props.resumeData.email,
        location: props.resumeData.location,
        personal_summary: props.resumeData.personal_summary
      };
      emit('edit', 'basic_info', editData);
    };
    const toggleBioExpand = () => {
      emit('toggleBioExpand');
    };

    // 8. 工具函数
    const calculateAge = birthDate => {
      if (!birthDate) return '';
      const birth = new Date(birthDate);
      const today = new Date();
      let age = today.getFullYear() - birth.getFullYear();
      const monthDiff = today.getMonth() - birth.getMonth();
      if (monthDiff < 0 || monthDiff === 0 && today.getDate() < birth.getDate()) {
        age--;
      }
      return `${age}岁`;
    };
    const formatGender = gender => {
      const genderMap = {
        male: '男',
        female: '女',
        other: '其他'
      };
      return genderMap[gender] || '未填写';
    };
    const formatDate = dateString => {
      if (!dateString) return '';
      const date = new Date(dateString);
      return date.toLocaleDateString('zh-CN', {
        year: 'numeric',
        month: 'long',
        day: 'numeric'
      });
    };

    // 调试辅助函数
    const logResumeData = () => {
      const plainData = {
        name: props.resumeData.name,
        gender: props.resumeData.gender,
        phone: props.resumeData.phone,
        email: props.resumeData.email,
        location: props.resumeData.location,
        birth_date: props.resumeData.birth_date,
        personal_summary: props.resumeData.personal_summary,
        created_at: props.resumeData.created_at,
        updated_at: props.resumeData.updated_at
      };
      console.log('BasicInfo data:', plainData);
    };

    // 添加头像上传处理函数
    const handleAvatarUpload = async file => {
      try {
        loading.value = true;
        console.log('开始上传职业头像:', {
          file,
          size: file.size,
          type: file.type
        });
        const response = await profile.uploadAvatar(file);
        console.log('职业头像上传响应:', response);
        if (response?.data?.code === 200) {
          showToast('职业头像上传成功', 'success');

          // 更新本地数据
          if (props.resumeData) {
            props.resumeData.avatar = response.data.data.avatar;

            // 触发父组件更新
            emit('update', {
              type: 'avatar',
              value: response.data.data.avatar
            });

            // 触发全局头像更新事件
            eventBus.emit('avatar-updated', response.data.data.avatar);

            // 更新 accountStore 中的头像
            const accountStore = useAccountStore();
            accountStore.updateAvatar(response.data.data.avatar);
          }
        } else {
          throw new Error(response.data?.message || '上传失败');
        }
      } catch (error) {
        console.error('职业头像上传失败:', error);
        showToast(error.message || '职业头像上传失败，请稍后重试', 'error');
      } finally {
        loading.value = false;
        showAvatarCropper.value = false;
      }
    };

    // 添加图片加载错误处理
    const handleImageError = e => {
      e.target.src = defaultAvatarImage;
    };

    // 添加文件验证
    const validateFile = file => {
      // 验证文件类型
      if (!file.type.startsWith('image/')) {
        showToast('请选择图片文件', 'error');
        return false;
      }

      // 验证文件大小（例如限制为 2MB）
      const maxSize = 20 * 1024 * 1024; // 2MB
      if (file.size > maxSize) {
        showToast('图片大小不能超过 2MB', 'error');
        return false;
      }
      return true;
    };

    // 添加状态
    const showAvatarCropper = ref(false);
    const avatarUploadRef = ref(null);
    const loading = ref(false);

    // 监听 resumeData 变化
    watch(() => props.resumeData, newVal => {
      if (newVal) {
        // 强制更新组件
        nextTick(() => {
          if (props.resumeData?.avatar) {
            const img = new Image();
            img.src = userAvatar.value;
          }
        });
      }
    }, {
      deep: true
    });
    const __returned__ = {
      props,
      emit,
      store,
      pageLoading,
      basicInfo,
      formatBasicInfo,
      userAvatar,
      currentGender,
      handleFileChange,
      handleEdit,
      toggleBioExpand,
      calculateAge,
      formatGender,
      formatDate,
      logResumeData,
      handleAvatarUpload,
      handleImageError,
      validateFile,
      showAvatarCropper,
      avatarUploadRef,
      loading,
      ref,
      computed,
      nextTick,
      watch,
      onMounted,
      get ElMessage() {
        return ElMessage;
      },
      get defaultAvatarImage() {
        return defaultAvatarImage;
      },
      get useStore() {
        return useStore;
      },
      get MEDIA_URL() {
        return MEDIA_URL;
      },
      get showToast() {
        return showToast;
      },
      get PencilIcon() {
        return PencilIcon;
      },
      get PhoneIcon() {
        return PhoneIcon;
      },
      get EnvelopeIcon() {
        return EnvelopeIcon;
      },
      get MapPinIcon() {
        return MapPinIcon;
      },
      get BriefcaseIcon() {
        return BriefcaseIcon;
      },
      get ClockIcon() {
        return ClockIcon;
      },
      get CurrencyYenIcon() {
        return CurrencyYenIcon;
      },
      get DocumentTextIcon() {
        return DocumentTextIcon;
      },
      get CameraIcon() {
        return CameraIcon;
      },
      get UserIcon() {
        return UserIcon;
      },
      get CalendarIcon() {
        return CalendarIcon;
      },
      get ChevronDownIcon() {
        return ChevronDownIcon;
      },
      get ChevronUpIcon() {
        return ChevronUpIcon;
      },
      get CakeIcon() {
        return CakeIcon;
      },
      get PencilSquareIcon() {
        return PencilSquareIcon;
      },
      get profile() {
        return profile;
      },
      get eventBus() {
        return eventBus;
      },
      get Dialog() {
        return Dialog;
      },
      get DialogPanel() {
        return DialogPanel;
      },
      get DialogTitle() {
        return DialogTitle;
      },
      get TransitionChild() {
        return TransitionChild;
      },
      get TransitionRoot() {
        return TransitionRoot;
      },
      ProfileAvatarUpload,
      get useAccountStore() {
        return useAccountStore;
      }
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
