import request from '@/utils/request'
import resumeApi from '@/api/resume'

// 组件相关接口
const component = {
  // 获取组件分类
  getCategories() {
    return resumeApi.getComponentCategories()
  },

  // 获取组件列表
  getComponents(categoryId) {
    return resumeApi.getComponents(categoryId)
  }
}

// 模板相关接口
const template = {
  // 获取模板列表
  getTemplates() {
    return resumeApi.getTemplates()
  },

  // 获取模板详情
  getTemplateDetail(id) {
    return resumeApi.getTemplateDetail(id)
  }
}

// 简历相关接口
const resume = {
  // 创建简历
  create(data) {
    return resumeApi.createResume(data)
  },

  // 获取简历详情
  getDetail(id) {
    return resumeApi.getResumeDetail(id)
  },

  // 更新简历
  update(id, data) {
    return resumeApi.updateResume(id, data)
  },

  // 删除简历
  delete(id) {
    return resumeApi.deleteResume(id)
  }
}

export default {
  component,
  template,
  resume
}
