import request from '@/utils/request'

export const membership = {
  // 获取会员等级列表
  getTiers: () => {
    return request.get('/membership/tiers/')
  },

  // 创建会员订单
  createOrder: (data) => {
    return request.post('/membership/orders/', data)
  },

  // 创建支付
  createPayment: (data) => {
    return request.post('/membership/payment/create/', data)
  },

  // 获取用户会员信息
  getUserMembership: () => {
    return request.get('/membership/membership/')
  },

  // 获取积分信息
  getPoints: () => {
    return request.get('/membership/points/')
  },

  // 获取积分记录
  getPointRecords: () => {
    return request.get('/membership/points/records/')
  }
}
