import { ref, computed, watch } from 'vue';
import CommunityNavigation from '@/components/community/CommunityNavigation.vue';

// 分类相关状态

export default {
  __name: 'HomeView',
  props: {
    currentCategory: {
      type: String,
      default: 'all'
    }
  },
  setup(__props, {
    expose: __expose
  }) {
    __expose();
    const currentMainCategory = ref('all');
    const currentSort = ref('popular');

    // 监听父组件传递的分类变化
    const props = __props;

    // 当父组件的分类变化时，更新本地分类
    watch(() => props.currentCategory, newValue => {
      currentMainCategory.value = newValue;
    });
    const __returned__ = {
      currentMainCategory,
      currentSort,
      props,
      ref,
      computed,
      watch,
      CommunityNavigation
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
