import request from '@/utils/request'

// 获取会员等级列表
export function getTiers() {
  return request({
    url: '/api/v1/membership/tiers',
    method: 'get'
  })
}

// 创建会员订单
export function createOrder(data) {
  return request({
    url: '/api/v1/membership/purchase',
    method: 'post',
    data
  })
}

// 创建支付
export function createPayment(data) {
  return request({
    url: '/api/v1/membership/payment',
    method: 'post',
    data
  })
}

// 查询订单状态
export function queryOrderStatus(orderNo) {
  return request({
    url: `/api/v1/membership/order/${orderNo}/status`,
    method: 'get'
  })
}
