import { defineStore } from 'pinia'
import { auth } from '@/api/auth'
import { account } from '@/api/account'

export const useUserStore = defineStore('user', {
  state: () => ({
    isLoggedIn: false,
    userInfo: null,
    rememberMe: false  // 添加记住我状态
  }),

  actions: {
    async login(credentials, rememberMe = false) {
      try {
        const response = await auth.loginWithPassword({
          account: credentials.account.trim(),
          password: credentials.password
        })

        if (response?.data?.code === 200) {
          const { access, refresh } = response.data.data

          // 根据记住我选项设置不同的过期时间
          const expiresIn = rememberMe ? 7 * 24 * 60 * 60 * 1000 : 24 * 60 * 60 * 1000
          const expiresAt = new Date().getTime() + expiresIn

          // 存储 token 和过期时间
          localStorage.setItem('token', access)
          localStorage.setItem('refresh_token', refresh)
          localStorage.setItem('token_expires_at', expiresAt)
          localStorage.setItem('remember_me', rememberMe)

          // 如果记住我，保存账号
          if (rememberMe) {
            localStorage.setItem('remembered_account', credentials.account)
          } else {
            localStorage.removeItem('remembered_account')
          }

          this.isLoggedIn = true
          this.rememberMe = rememberMe

          // 获取用户信息
          await this.getUserInfo()
          return true
        }
        return false
      } catch (error) {
        console.error('Login failed:', error)
        throw error
      }
    },

    async logout() {
      try {
        await auth.logout()
      } finally {
        this.clearUserData()
      }
    },

    clearUserData() {
      localStorage.removeItem('token')
      localStorage.removeItem('refresh_token')
      localStorage.removeItem('token_expires_at')
      localStorage.removeItem('remember_me')
      this.isLoggedIn = false
      this.userInfo = null
      this.rememberMe = false
    },

    async getUserInfo() {
      try {
        const response = await account.getUserInfo()
        if (response?.data?.code === 200) {
          this.userInfo = response.data.data
          return this.userInfo
        }
        return null
      } catch (error) {
        console.error('Get user info failed:', error)
        throw error
      }
    },

    // 检查 token 是否过期
    isTokenExpired() {
      const expiresAt = localStorage.getItem('token_expires_at')
      if (!expiresAt) return true
      return new Date().getTime() > parseInt(expiresAt)
    }
  }
})
