    const vipButtonText = ref('开通会员');

    // 积分相关数据
    const pointsInfo = ref({
      balance: 0,
      level: 1,
      sign_in_days: 0,
      total_earned: 0
    });
    const signInInfo = ref({
      days: 0,
      nextReward: null
    });
    const canSignIn = ref(true);
    const loading = ref(false);
    const currentFilter = ref('all');
    const currentTab = ref('points');
    const pointsRecords = ref([]);
    const hasMore = ref(false);
    const page = ref(1);
    const expandedOrders = ref([]);

    // 会员权益列表
    const memberBenefits = [{
      key: 'unlimited',
      name: '无限使用',
      desc: '无限制使用所有功能',
      icon: SparklesIcon
    }
    // ... 其他权益配置
    ];

    // 积分筛选选项
    const pointsFilters = [{
      label: '全部',
      value: 'all'
    }, {
      label: '获得',
      value: 'earned'
    }, {
      label: '使用',
      value: 'used'
    }];

    // tabs配置
    const tabs = [{
      key: 'points',
      name: '积分规则'
    }, {
      key: 'privileges',
      name: '等级权益'
    }, {
      key: 'orders',
      name: '订单记录'
    }];

    // 积分规则
    const pointRules = [{
      action: '每日签到',
      points: '+1~10',
      description: '连续签到可获得更多积分奖励',
      icon: CalendarDaysIcon,
      bgColor: 'bg-blue-50',
      iconColor: 'text-blue-500',
      borderColor: 'border-blue-100',
      textColor: 'text-blue-600'
    }
    // ... 其他规则配置
    ];

    // 计算属性
    const filteredRecords = computed(() => {
      if (!pointsRecords.value) return [];
      if (currentFilter.value === 'all') return pointsRecords.value;
      return pointsRecords.value.filter(record => currentFilter.value === 'earned' ? record.points > 0 : record.points < 0);
    });

    // 添加会员信息的响应式数据
    const membershipInfo = ref({
      is_active: false,
      tier: null,
      expire_at: null
    });

    // 添加获取会员信息的方法
    const fetchMembershipInfo = async () => {
      try {
        const response = await user.getUserInfo();
        if (response?.data?.code === 200) {
          const userInfo = response.data.data;

          // 更新会员状态显示
          if (userInfo.is_vip && userInfo.vip_expire_time && new Date(userInfo.vip_expire_time) > new Date()) {
            // 是会员且未过期
            vipTypeText.value = userInfo.vip_status || '高级会员';
            const expireDate = formatDate(userInfo.vip_expire_time, 'YYYY-MM-DD');
            vipExpireText.value = `有效期至 ${expireDate}`;
            vipButtonText.value = '续费会员';

            // 更新会员信息
            membershipInfo.value = {
              is_active: true,
              tier: {
                name: userInfo.vip_status,
                type: userInfo.vip_type
              },
              expire_at: userInfo.vip_expire_time
            };
          } else {
            // 不是会员或已过期
            vipTypeText.value = userInfo.vip_type === 'free' ? '免费用户' : '会员已过期';
            vipExpireText.value = '开通会员享受更多权益';
            vipButtonText.value = '开通会员';

            // 更新会员信息
            membershipInfo.value = {
              is_active: false,
              tier: null,
              expire_at: null
            };
          }
        }
      } catch (error) {
        console.error('获取会员信息失败:', error);
        // 设置默认状态
        vipTypeText.value = '免费用户';
        vipExpireText.value = '开通会员享受更多权益';
        vipButtonText.value = '开通会员';
        membershipInfo.value = {
          is_active: false,
          tier: null,
          expire_at: null
        };
      }
    };

    // 修改 handleVipButton 方法
    const handleVipButton = () => {
      if (membershipInfo.value.is_active) {
        // 续费逻辑
        console.log('续费会员');
        // TODO: 实现续费逻辑
      } else {
        // 开通逻辑
        console.log('开通会员');
        // TODO: 实现开通逻辑
      }
    };

    // 方法
    const handleSignIn = async () => {
      if (!canSignIn.value) return;
      try {
        const response = await membership.signIn();
        if (response?.data?.code === 200) {
          ElMessage.success('签到成功');
          canSignIn.value = false;
          await fetchPointsInfo();
        }
      } catch (error) {
        console.error('签到失败:', error);
        ElMessage.error('签到失败，请稍后重试');
      }
    };
    const fetchPointsInfo = async () => {
      try {
        const response = await membership.getPointsInfo();
        if (response?.data?.code === 200) {
          pointsInfo.value = response.data.data;
        } else {
          // 处理其他响应状态
          console.log('获取积分信息失败:', response);
        }
      } catch (error) {
        console.error('获取积分信息失败:', error);
      }
    };
    const fetchPointsRecords = async () => {
      if (loading.value) return;
      loading.value = true;
      try {
        const response = await membership.getPointsRecords({
          page: page.value
        });
        if (response?.data?.code === 200) {
          // 确保 records 是数组
          const records = response.data.data?.records || [];
          const has_more = response.data.data?.has_more || false;
          if (page.value === 1) {
            pointsRecords.value = records;
          } else {
            pointsRecords.value.push(...records);
          }
          hasMore.value = has_more;
        }
      } catch (error) {
        console.error('获取积分记录失败:', error);
      } finally {
        loading.value = false;
      }
    };
    const loadMore = () => {
      page.value++;
      fetchPointsRecords();
    };
    const getLevelTitle = level => {
      const titles = ['初级用户', '普通用户', '高级用户', '资深用户', '专家用户'];
      return titles[level - 1] || titles[0];
    };
    const getLevelRange = level => {
      const ranges = {
        1: '0-100积分',
        2: '101-500积分',
        3: '501-2000积分',
        4: '2001-10000积分',
        5: '10000积分以上'
      };
      return ranges[level];
    };
    const getLevelProgress = (level, balance) => {
      const thresholds = [0, 100, 500, 2000, 10000];
      const currentThreshold = thresholds[level - 1] || 0;
      const nextThreshold = thresholds[level] || thresholds[thresholds.length - 1];
      return Math.min((balance - currentThreshold) / (nextThreshold - currentThreshold) * 100, 100);
    };
    const getNextLevelPoints = (level, balance) => {
      const thresholds = [0, 100, 500, 2000, 10000];
      if (level >= thresholds.length) return 0;
      return Math.max(thresholds[level] - balance, 0);
    };
    const toggleOrderDetail = orderNo => {
      const index = expandedOrders.value.indexOf(orderNo);
      if (index > -1) {
        expandedOrders.value.splice(index, 1);
      } else {
        expandedOrders.value.push(orderNo);
      }
    };
    const getLevelThreshold = level => {
      const thresholds = [0, 100, 500, 2000, 10000];
      return thresholds[level] || thresholds[thresholds.length - 1];
    };

    // 获取签到提示
    const getSignInTip = () => {
      if (!signInInfo.value.nextReward) return '';
      return `再签到 ${signInInfo.value.nextReward.days} 天可获得 ${signInInfo.value.nextReward.points} 积分奖励`;
    };

    // 获取等级特权
    const getLevelPrivileges = level => {
      const privileges = {
        1: [{
          title: '基础功能',
          description: '可使用所有基础功能'
        }, {
          title: '每日签到',
          description: '每日签到获取积分'
        }],
        2: [{
          title: '高级功能',
          description: '解锁部分高级功能'
        }, {
          title: '积分加速',
          description: '签到积分1.2倍'
        }],
        3: [{
          title: '专业功能',
          description: '解锁全部高级功能'
        }, {
          title: '积分加速',
          description: '签到积分1.5倍'
        }],
        4: [{
          title: '特权服务',
          description: '享受专属客服服务'
        }, {
          title: '积分加速',
          description: '签到积分2倍'
        }],
        5: [{
          title: 'VIP特权',
          description: '享受最高级别服务'
        }, {
          title: '积分加速',
          description: '签到积分3倍'
        }]
      };
      return privileges[level] || privileges[1];
    };

    // 格式化订单状态
    const formatOrderStatus = status => {
      const statusMap = {
        pending: '待支付',
        paid: '已支付',
        cancelled: '已取消',
        refunded: '已退款'
      };
      return statusMap[status] || status;
    };

    // 格式化支付方式
    const formatPaymentMethod = method => {
      const methodMap = {
        alipay: '支付宝',
        wechat: '微信支付',
        balance: '余额支付'
      };
      return methodMap[method] || method;
    };

    // 格式化会员类型
    const formatMembershipType = order => {
      return `${order.tier_name || '普通会员'} (${order.duration}个月)`;
    };

    // 处理支付
    const handlePayment = order => {
      // 处理支付逻辑
      console.log('处理支付:', order);
    };

    // 显示订单详情
    const showOrderDetail = order => {
      // 显示订单详情逻辑
      console.log('显示订单详情:', order);
    };

    // 添加 orders 数据
    const orders = ref([]);

    // 在组件挂载时获取数据
    onMounted(async () => {
      try {
        await Promise.all([fetchMembershipInfo(),
        // 使用用户信息接口获取会员状态
        fetchPointsInfo(), fetchPointsRecords()]);
      } catch (error) {
        console.error('初始化数据失败:', error);
        pointsRecords.value = [];
      }
    });
    const __returned__ = {
      vipTypeText,
      vipExpireText,
      vipButtonText,
      pointsInfo,
      signInInfo,
      canSignIn,
      loading,
      currentFilter,
      currentTab,
      pointsRecords,
      hasMore,
      page,
      expandedOrders,
      memberBenefits,
      pointsFilters,
      tabs,
      pointRules,
      filteredRecords,
      membershipInfo,
      fetchMembershipInfo,
      handleVipButton,
      handleSignIn,
      fetchPointsInfo,
      fetchPointsRecords,
      loadMore,
      getLevelTitle,
      getLevelRange,
      getLevelProgress,
      getNextLevelPoints,
      toggleOrderDetail,
      getLevelThreshold,
      getSignInTip,
      getLevelPrivileges,
      formatOrderStatus,
      formatPaymentMethod,
      formatMembershipType,
      handlePayment,
      showOrderDetail,
      orders,
      ref,
      computed,
      onMounted,
      get ElMessage() {
        return ElMessage;
      },
      get CalendarDaysIcon() {
        return CalendarDaysIcon;
      },
      get StarIcon() {
        return StarIcon;
      },
      get TrophyIcon() {
        return TrophyIcon;
      },
      get CheckCircleIcon() {
        return CheckCircleIcon;
      },
      get ChevronDownIcon() {
        return ChevronDownIcon;
      },
      get SparklesIcon() {
        return SparklesIcon;
      },
      get membership() {
        return membership;
      },
      get user() {
        return user;
      },
      get formatDate() {
        return formatDate;
      }
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
