      }
    }, {
      key: 'community',
      label: '社区',
      icon: UsersIcon,
      handler: () => router.push('/community')
    }, {
      key: 'portfolio',
      label: '创作集',
      icon: PhotoIcon,
      handler: () => router.push('/portfolio')
    }, {
      key: 'profile',
      label: '我的',
      icon: UserCircleIcon,
      handler: () => router.push('/user?tab=profile')
    }, {
      key: 'settings',
      label: '设置',
      icon: Cog6ToothIcon,
      handler: () => router.push('/user?tab=account')
    }];
    const isActiveAction = key => {
      if (key === 'more') return false;
      if (key === 'community') return route.path.startsWith('/community');
      if (key === 'portfolio') return route.path.startsWith('/portfolio');
      if (key === 'profile') return route.path.includes('/user') && route.query.tab === 'profile';
