    const settingsMenuGroups = [{
      title: '设置',
      items: [{
        name: '账号设置',
        path: '/user?tab=account',
        icon: Cog6ToothIcon
      }, {
        name: '隐私设置',
        path: '/user?tab=privacy',
        icon: ShieldCheckIcon
      }]
    }];

    // 根据当前路由计算要显示的菜单组
    const currentMenuGroups = computed(() => {
      if (route.path.startsWith('/community')) {
        return communityMenuGroups;
      }
      if (route.path.startsWith('/portfolio')) {
        return portfolioMenuGroups;
      }
      if (route.path.includes('/user')) {
        if (route.query.tab === 'account' || route.query.tab === 'messages' || route.query.tab === 'privacy') {
          return settingsMenuGroups;
        }
        return profileMenuGroups;
      }
      return communityMenuGroups; // 默认显示社区菜单
    });
    const __returned__ = {
