import request from '@/utils/request'

export const user = {
  // 获取用户信息
  getUserInfo: () => {
    return request.get('/api/v1/users/userInfo/')
  },

  // 更新用户信息
  updateUserInfo: (data) => {
    const formData = new FormData()
    // 遍历对象的所有属性
    Object.keys(data).forEach(key => {
      if (data[key] !== null && data[key] !== undefined) {
        // 如果是文件类型，直接添加
        if (key === 'avatar' && data[key] instanceof File) {
          formData.append('avatar', data[key])
        } 
        // 其他字段正常处理
        else {
          formData.append(key, data[key].toString())
        }
      }
    })

    return request.put('/api/v1/users/userInfo/', formData, {
      headers: {
        'Content-Type': 'multipart/form-data'
      }
    })
  },

  // 更新用户名
  updateUsername: (data) => {
    return request.post('/api/v1/users/account/username/', {
      username: data.username
    })
  },

  // 更换手机号
  changePhone: (data) => {
    return request.post('/api/v1/users/account/phone/', {
      phone: data.phone,
      code: data.code
    })
  },

  // 修改密码
  updatePassword: (data) => {
    return request.post('/api/v1/users/account/password/', {
      old_password: data.old_password,
      new_password: data.new_password,
      confirm_password: data.confirm_password
    })
  },

  // 上传背景图
  uploadBackground: (file) => {
    const formData = new FormData()
    formData.append('background', file)
    return request.post('/api/v1/users/userInfo/background/', formData, {
      headers: {
        'Content-Type': 'multipart/form-data'
      }
    })
  },
}

export const updateNickname = (nickname) => {
  return user.updateUsername({ username: nickname })
}

export default user
