import { ref, onMounted, computed } from 'vue';
import { useRouter } from 'vue-router';
import { ElMessage } from 'element-plus';
import { useStore } from 'vuex';
import { useNickname } from '@/composables/useNickname';
import { MEDIA_URL } from '@/config';
import defaultAvatarImage from '@/assets/images/default-avatar.png';
import defaultBackground from '@/assets/images/default-background.jpg';
import { showToast } from '@/components/ToastMessage';
import { UserIcon, UserCircleIcon, DocumentTextIcon, BriefcaseIcon, StarIcon, CameraIcon, PhotoIcon, FolderIcon, Cog6ToothIcon, IdentificationIcon, HomeIcon, MapPinIcon, QuoteIcon, PencilSquareIcon, CrownIcon, PhoneIcon, EnvelopeIcon, SparklesIcon, PencilIcon, DocumentIcon, ChevronRightIcon, PlusIcon, TrashIcon, ChatBubbleLeftRightIcon, QuestionMarkCircleIcon, InboxArrowDownIcon, EyeIcon, BookmarkIcon, ArrowTopRightOnSquareIcon, GlobeAltIcon } from '@heroicons/vue/24/outline';
