import AvatarUploadDialog from '../dialogs/AvatarUploadDialog.vue';
const storageLimit = 10 * 1024 * 1024 * 1024; // 10GB

export default {
  __name: 'UserHome',
  setup(__props, {
    expose: __expose
  }) {
    __expose();
    const router = useRouter();
    const store = useStore();
    const nickname = useNickname();

    // 用户信息状态
    const userInfo = ref({
      nickname: '',
      avatar: '',
      coverImage: '',
      gender: '',
      isVip: false,
      vipExpireDate: '',
      uid: '',
      jobTitle: ''
    });

    // 添加状态
    const showAvatarUpload = ref(false);
    const loading = ref(false);

    // 昵称修改相关
    const showNicknameModal = ref(false);
    const openNicknameModal = () => {
      nickname.state.value = userInfo.value.nickname;
      showNicknameModal.value = true;
    };
    const closeNicknameModal = () => {
      showNicknameModal.value = false;
      nickname.state.value = '';
    };
    const handleNicknameUpdate = async () => {
      const success = await nickname.handleUpdate();
      if (success) {
        closeNicknameModal();
        // 更新本地用户信息
        userInfo.value.nickname = nickname.state.value;
      }
    };

    // 获取用户基本信息
    const fetchUserInfo = async () => {
      try {
        const response = await profile.getData();
        if (response.data?.code === 200 && response.data?.data) {
          const data = response.data.data;
          userInfo.value = {
            nickname: data.user?.username || data.basic_info?.name || '',
            avatar: formatImageUrl(data.basic_info?.avatar),
            coverImage: formatImageUrl(data.basic_info?.background),
            gender: data.basic_info?.gender,
            isVip: true,
            vipExpireDate: '2024-12-31',
            uid: data.user?.uid || '',
            jobTitle: data.job_intention?.job_title
          };
        }
      } catch (error) {
        console.error('获取用户信息失败:', error);
        showToast('获取用户信息失败', 'error');
      }
    };

    // 修改头像上传处理函数
    const handleAvatarUpload = async file => {
      try {
        loading.value = true;
        const formData = new FormData();
        formData.append('avatar', file);
        const response = await profile.uploadAvatar(formData);
        if (response.data?.code === 200) {
          showToast('头像上传成功', 'success');
          const avatarUrl = response.data.data.avatar;
          userInfo.value.avatar = formatImageUrl(avatarUrl);
          eventBus.emit('avatar-updated', avatarUrl);
          showAvatarUpload.value = false; // 关闭弹窗
        } else {
          throw new Error(response.data?.message || '上传失败');
        }
      } catch (error) {
        console.error('头像上传失败:', error);
        showToast(error.message || '头像上传失败，请稍后重试', 'error');
      } finally {
        loading.value = false;
      }
    };

    // 处理背景图上传
    const handleCoverUpload = async event => {
      const file = event.target.files[0];
      if (!file) return;
      if (!validateFile(file)) {
        return;
      }
      const formData = new FormData();
      formData.append('background', file);
      try {
        const response = await profile.uploadBackground(formData);
        if (response.data?.code === 200) {
          showToast('背景图更新成功', 'success');
          const backgroundUrl = response.data.data.background;
          userInfo.value.coverImage = formatImageUrl(backgroundUrl);
          store.commit('UPDATE_BACKGROUND', backgroundUrl);
        }
      } catch (error) {
        console.error('背景图更新失败:', error);
        showToast('背景图更新失败', 'error');
      } finally {
        event.target.value = '';
      }
    };

    // 工具函数
    const formatImageUrl = url => {
      if (!url) return '';
      return url.startsWith('http') ? url : `${MEDIA_URL}${url}`;
    };
    const validateFile = file => {
      // 验证文件类型
      if (!file.type.startsWith('image/')) {
        showToast('请选择图片文件', 'error');
        return false;
      }

      // 验证文件大小（限制为 2MB）
      const maxSize = 2 * 1024 * 1024;
      if (file.size > maxSize) {
        showToast('图片大小不能超过 2MB', 'error');
        return false;
      }
      return true;
    };

    // 错误处理函数
    const handleImageError = e => {
      e.target.src = defaultAvatarImage;
    };
    const handleBackgroundError = e => {
      e.target.src = defaultBackground;
    };

