    const handlePortfolioImageError = e => {
      // 使用在线占位图服务
      e.target.src = 'https://via.placeholder.com/120x120?text=作品封面';
    };

    // 处理作品集相关操作
    const handleEditPortfolio = portfolio => {
      router.push(`/portfolio/edit/${portfolio.id}`);
    };
    const handleDeletePortfolio = async portfolio => {
      if (confirm('确定要删除这个作品吗？')) {
        try {
          // await profile.deletePortfolio(portfolio.id)
