      count: '0'
    }];
    const currentFavTab = ref('articles');

    // 获取当前收藏标签的显示文本
    const getCurrentFavTabLabel = () => {
      const tab = favoritesTabs.find(tab => tab.key === currentFavTab.value);
      return tab ? tab.label : '';
    };

    // 获取对应的探索路由
    const getFavoritesExploreRoute = () => {
      const routeMap = {
        articles: '/articles',
        qa: '/qa',
