import request from '@/utils/request'

export const user = {
  // 获取用户信息
  getUserInfo: () => {
    return request.get('/api/v1/users/userInfo/')
  },

  // 更新用户头像
  updateAvatar: (data) => {
    const formData = new FormData()
    formData.append('avatar', data)
    return request.post('/api/v1/users/userInfo/avatar/', formData, {
      headers: {
        'Content-Type': 'multipart/form-data'
      }
    })
  },

  // 更新用户背景图
  updateBackground: (data) => {
    const formData = new FormData()
    formData.append('background', data)
    return request.post('/api/v1/users/userInfo/background/', formData, {
      headers: {
        'Content-Type': 'multipart/form-data'
      }
    })
  },

  // 更新用户基本信息
  updateUserInfo: (data) => {
    const formData = new FormData()
    // 遍历对象，将所有非空值添加到 FormData
    Object.keys(data).forEach(key => {
      if (data[key] !== null && data[key] !== undefined && data[key] !== '') {
        formData.append(key, data[key])
      }
    })

    return request.put('/api/v1/users/userInfo/', formData, {
      headers: {
        'Content-Type': 'multipart/form-data'
      }
    })
  },

  // 获取用户资料完整度
  getProfileCompleteness: () => {
    return request.get('/api/v1/users/account/completeness/')
  }
}

export const updateNickname = (nickname) => {
  return user.updateUsername({ username: nickname })
}

export default user
