import request from '@/utils/request'

export const user = {
  // 获取用户信息
  getUserInfo: () => {
    return request.get('/api/v1/users/userInfo/')
  },

  // 更新用户头像
  updateAvatar: (formData) => {
    return request.post('/api/v1/users/account/avatar/', formData, {
      headers: {
        'Content-Type': 'multipart/form-data'
      }
    })
  },

  // 更新用户背景图
  updateBackground: (formData) => {
    return request.post('/api/v1/users/account/background/', formData, {
      headers: {
        'Content-Type': 'multipart/form-data'
      }
    })
  },

  // 更新用户基本信息
  updateUserInfo: (data) => {
    return request.post('/api/v1/users/account/update/', data)
  },

  // 获取用户资料完整度
  getProfileCompleteness: () => {
    return request.get('/api/v1/users/account/completeness/')
  }
}

export const updateNickname = (nickname) => {
  return user.updateUsername({ username: nickname })
}

export default user
