import { ref, computed } from 'vue'
import { useUserStore } from '@/stores/user'

export function useChangePassword() {
  const userStore = useUserStore()
  const state = ref({
    oldPassword: '',
    newPassword: '',
    confirmPassword: '',
    loading: false,
    error: '',
    strength: 0
  })

  const validatePassword = (password) => {
    if (!password) return '请输入密码'
    if (password.length < 8) return '密码长度不能少于8位'
    if (!/[A-Za-z]/.test(password)) return '密码必须包含字母'
    if (!/\d/.test(password)) return '密码必须包含数字'
    return ''
  }

  const checkPasswordStrength = (password) => {
    let score = 0

    if (password.length >= 8) score += 1
    if (password.length >= 12) score += 1

    if (/[a-z]/.test(password)) score += 1
    if (/[A-Z]/.test(password)) score += 1
    if (/\d/.test(password)) score += 1
    if (/[!@#$%^&*]/.test(password)) score += 1

    return score
  }

  const updatePasswordStrength = (password) => {
    state.value.strength = checkPasswordStrength(password)
  }

  const strengthText = computed(() => {
    const strength = state.value.strength
    if (strength <= 1) return '弱'
    if (strength <= 3) return '中'
    if (strength <= 5) return '强'
    return '非常强'
  })

  const strengthTextClass = computed(() => {
    const strength = state.value.strength
    if (strength <= 1) return 'text-red-500'
    if (strength <= 3) return 'text-yellow-500'
    if (strength <= 5) return 'text-green-500'
    return 'text-green-600'
  })

  const strengthColorClass = computed(() => {
    const strength = state.value.strength
    if (strength <= 1) return 'bg-red-500'
    if (strength <= 3) return 'bg-yellow-500'
    if (strength <= 5) return 'bg-green-500'
    return 'bg-green-600'
  })

  return {
    state,
    validatePassword,
    updatePasswordStrength,
    strengthText,
    strengthTextClass,
    strengthColorClass,
    userStore
  }
}
