import "core-js/modules/esnext.iterator.constructor.js";
import "core-js/modules/esnext.iterator.filter.js";
import TemplateNavigation from '@/components/resume/TemplateNavigation.vue';
import TemplateCard from '@/components/resume/ResumeCard.vue';
import { ref, computed } from 'vue';

// 当前选中的分类

export default {
  __name: 'CampusView',
  setup(__props, {
    expose: __expose
  }) {
    __expose();
    const currentCategory = ref('all');

    // 模板数据
    const templates = ref([{
      id: 1,
      title: '简约校园简历模板',
      cover: 'https://picsum.photos/600/800?random=1',
      isPro: false,
      type: 'graduate'
    }, {
      id: 2,
      title: '专业实习生简历模板',
      cover: 'https://picsum.photos/600/800?random=2',
      isPro: true,
      type: 'internship'
    }, {
      id: 3,
      title: '研究生学术简历模板',
      cover: 'https://picsum.photos/600/800?random=3',
      isPro: true,
      type: 'research'
    }, {
      id: 4,
      title: '竞赛获奖简历模板',
      cover: 'https://picsum.photos/600/800?random=4',
      isPro: false,
      type: 'competition'
    }]);

    // 筛选逻辑
    const filteredTemplates = computed(() => {
      if (currentCategory.value === 'all') {
        return templates.value;
      }
      return templates.value.filter(template => template.type === currentCategory.value);
    });
    const __returned__ = {
      currentCategory,
      templates,
      filteredTemplates,
      TemplateNavigation,
      TemplateCard,
      ref,
      computed
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
