import request from '@/utils/request'

// 获取模板分类列表
export function getCategories() {
  return request({
    url: '/api/v1/resume/template-categories/',
    method: 'get'
  })
}

// 保存模板
export function saveTemplate(data) {
  const formData = new FormData()

  // 添加基本字段
  formData.append('name', data.name)
  formData.append('category', data.category)
  formData.append('description', data.description)
  formData.append('is_vip', data.is_vip)

  // 添加缩略图
  if (data.thumbnail) {
    formData.append('thumbnail', data.thumbnail)
  }

  // 添加布局数据
  if (data.layout) {
    formData.append('layout', JSON.stringify(data.layout))
  }

  // 添加组件数据
  if (data.components) {
    formData.append('components', JSON.stringify(data.components))
  }

  // 添加主题数据
  if (data.theme) {
    formData.append('theme', JSON.stringify(data.theme))
  }

  return request({
    url: '/api/v1/resume/templates/',
    method: 'post',
    data: formData,
    headers: {
      'Content-Type': 'multipart/form-data'
    }
  })
}

// 获取模板列表
export function getTemplates(params) {
  return request({
    url: '/api/v1/resume/templates/',
    method: 'get',
    params
  })
}

// 获取模板详情
export function getTemplateDetail(id) {
  return request({
    url: `/api/v1/resume/templates/${id}/`,
    method: 'get'
  })
}

// 更新模板
export function updateTemplate(id, data) {
  const formData = new FormData()

  // 添加基本字段
  if (data.name) formData.append('name', data.name)
  if (data.category) formData.append('category', data.category)
  if (data.description) formData.append('description', data.description)
  if (typeof data.is_vip === 'boolean') formData.append('is_vip', data.is_vip)

  // 添加缩略图
  if (data.thumbnail) {
    formData.append('thumbnail', data.thumbnail)
  }

  // 添加布局数据
  if (data.layout) {
    formData.append('layout', JSON.stringify(data.layout))
  }

  // 添加组件数据
  if (data.components) {
    formData.append('components', JSON.stringify(data.components))
  }

  // 添加主题数据
  if (data.theme) {
    formData.append('theme', JSON.stringify(data.theme))
  }

  return request({
    url: `/api/v1/resume/templates/${id}/`,
    method: 'patch',
    data: formData,
    headers: {
      'Content-Type': 'multipart/form-data'
    }
  })
}

// 删除模板
export function deleteTemplate(id) {
  return request({
    url: `/api/v1/resume/templates/${id}/`,
    method: 'delete'
  })
}

// 提交模板审核
export function submitForReview(id) {
  return request({
    url: `/api/v1/resume/templates/${id}/submit_for_review/`,
    method: 'post'
  })
}
