import { defineStore } from 'pinia'
import profile from '@/api/profile'
import { MEDIA_URL } from '@/config/index'

export const useProfileStore = defineStore({
  id: 'profile',
  state: () => ({
    basicInfo: null,
    avatarUpdateTime: null,
    completeness: null,
    loading: false
  }),

  getters: {
    // 获取职业头像的完整 URL
    profileAvatarUrl: (state) => {
      const avatar = state.basicInfo?.avatar
      if (!avatar) return null
      // 如果已经是完整的 URL，直接返回
      if (avatar.startsWith('http')) return avatar
      // 如果是相对路径，需要去掉开头的 /media/
      const cleanPath = avatar.replace(/^\/media\//, '')
      return `${MEDIA_URL}/${cleanPath}`
    }
  },

  actions: {
    // 设置基本信息
    setBasicInfo(data) {
      this.basicInfo = data
    },

    // 更新基本信息
    async updateBasicInfo(data) {
      try {
        const response = await profile.updateBasicInfo(data)
        if (response?.data?.code === 200) {
          // 更新 store 中的数据
          if (!this.basicInfo) {
            this.basicInfo = {}
          }
          this.basicInfo = {
            ...this.basicInfo,
            basic_info: response.data.data
          }
          return response
        }
        throw new Error(response?.data?.message || '更新失败')
      } catch (error) {
        console.error('更新基本信息失败:', error)
        throw error
      }
    },

    // 获取完整度数据
    async fetchCompleteness() {
      try {
        const response = await profile.getCompleteness()
        if (response?.data?.code === 200) {
          this.completeness = response.data.data
          return response.data.data
        }
        throw new Error(response?.data?.message || '获取完整度数据失败')
      } catch (error) {
        console.error('获取完整度数据失败:', error)
        throw error
      }
    },

    // 更新头像
    async updateAvatar(file) {
      try {
        const response = await profile.uploadAvatar(file)
        if (response?.data?.code === 200) {
          const avatarUrl = response.data.data.avatar

          // 更新 store 状态
          if (!this.basicInfo) {
            this.basicInfo = {}
          }
          this.basicInfo.avatar = avatarUrl
          this.avatarUpdateTime = Date.now()

          return response
        }
        throw new Error(response?.data?.message || '上传失败')
      } catch (error) {
        console.error('职业头像上传失败:', error)
        throw error
      }
    },

    // 获取档案数据
    async fetchProfileData() {
      try {
        this.loading = true
        const response = await profile.getData()
        if (response?.data?.code === 200) {
          this.basicInfo = response.data.data
          return response.data.data
        }
        throw new Error(response?.data?.message || '获取档案数据失败')
      } catch (error) {
        console.error('获取档案数据失败:', error)
        throw error
      } finally {
        this.loading = false
      }
    }
  }
})
