import axios from 'axios'

// 创建 axios 实例
const api = axios.create({
  baseURL: '/api/v1/resume',
  timeout: 10000
})

// 请求拦截器
api.interceptors.request.use(
  config => {
    const token = localStorage.getItem('token')
    if (token) {
      config.headers.Authorization = `Bearer ${token}`
    }
    return config
  },
  error => {
    return Promise.reject(error)
  }
)

// API 方法
export const templateApi = {
  // 获取模板列表
  getTemplates() {
    return api.get('/templates/')
  },

  // 获取模板详情
  getTemplateById(id) {
    return api.get(`/templates/${id}/`)
  },

  // 更新模板
  updateTemplate(id, data) {
    return api.patch(`/templates/${id}/`, data)
  }
}

export const componentApi = {
  // 获取组件分类
  getCategories() {
    return api.get('/component-categories/main_categories/')
  },

  // 获取分类下的组件
  getComponentsByCategory(categoryId) {
    return api.get(`/component-categories/${categoryId}/components/`)
  },

  // 获取组件详情
  getComponentById(id) {
    return api.get(`/components/${id}/`)
  }
}

export const resumeApi = {
  // 获取简历列表
  getResumes() {
    return api.get('/resumes/')
  },

  // 获取简历详情
  getResumeById(id) {
    return api.get(`/resumes/${id}/`)
  },

  // 创建简历
  createResume(data) {
    return api.post('/resumes/', data)
  },

  // 更新简历
  updateResume(id, data) {
    return api.patch(`/resumes/${id}/`, data)
  },

  // 删除简历
  deleteResume(id) {
    return api.delete(`/resumes/${id}/`)
  }
}

export default {
  template: templateApi,
  component: componentApi,
  resume: resumeApi
}
