import { ref, computed } from 'vue';
import { useRouter, useRoute } from 'vue-router';
import { useStore } from 'vuex';
import { PlusIcon, UserIcon, ArrowsUpDownIcon, ChevronDownIcon, FireIcon, ClockIcon, HandThumbUpIcon, EyeIcon, HomeIcon, DocumentIcon, DocumentTextIcon, IdentificationIcon, BriefcaseIcon, AcademicCapIcon, LanguageIcon, SparklesIcon, UserGroupIcon } from '@heroicons/vue/24/outline';
export default {
  __name: 'ResumeNavigation',
  props: {
    currentCategory: {
      type: String,
      required: true
    },
    currentSort: {
      type: String,
      required: true
    }
  },
  emits: ['update:currentCategory', 'update:currentSort'],
  setup(__props, {
    expose: __expose,
    emit: __emit
  }) {
    __expose();
    const props = __props;
    const emit = __emit;
    const router = useRouter();
    const route = useRoute();
    const store = useStore();

    // 使用 Vuex store 的 isAuthenticated 状态
    const isAuthenticated = computed(() => store.state.isAuthenticated);

    // 主导航数据
    const mainNavs = [{
      name: '简历',
      path: '/templates/resume',
      icon: DocumentIcon
    }, {
      name: '求职信',
      path: '/templates/cover-letter',
      icon: DocumentTextIcon
    }];

    // 根据当前路由显示不同的分类选项
    const categories = computed(() => {
      const path = route.path;
      if (path.includes('resume')) {
        return [{
          id: 'all',
          name: '全部',
          icon: HomeIcon
        }, {
          id: 'fresh',
          name: '应届生',
          icon: AcademicCapIcon
        }, {
          id: 'experienced',
          name: '经验型',
          icon: BriefcaseIcon
        }, {
          id: 'manager',
          name: '管理层',
          icon: UserGroupIcon
        }, {
          id: 'creative',
          name: '创意型',
          icon: SparklesIcon
        }, {
          id: 'international',
          name: '国际化',
          icon: LanguageIcon
        }];
      } else if (path.includes('cover-letter')) {
        return [{
          id: 'all',
          name: '全部',
          icon: HomeIcon
        }, {
          id: 'general',
          name: '通用型',
          icon: DocumentTextIcon
        }, {
          id: 'career',
          name: '职业型',
          icon: BriefcaseIcon
        }, {
          id: 'academic',
          name: '学术型',
          icon: AcademicCapIcon
        }, {
          id: 'creative',
          name: '创意型',
          icon: SparklesIcon
        }];
      }
      // 首页分类
      return [{
        id: 'all',
        name: '全部',
        icon: HomeIcon
      }, {
        id: 'fresh',
        name: '应届生',
        icon: AcademicCapIcon
      }, {
        id: 'experienced',
        name: '经验型',
        icon: BriefcaseIcon
      }, {
        id: 'manager',
        name: '管理层',
        icon: UserGroupIcon
      }, {
        id: 'creative',
        name: '创意型',
        icon: SparklesIcon
      }, {
        id: 'international',
        name: '国际化',
        icon: LanguageIcon
      }];
    });

    // 排序菜单状态
    const showSortMenu = ref(false);

    // 排序选项
    const sortOptions = {
      popular: {
        label: '最受欢迎',
        icon: FireIcon
      },
      newest: {
        label: '最新发布',
        icon: ClockIcon
      },
      downloads: {
        label: '最多下载',
        icon: EyeIcon
      },
      likes: {
        label: '最多点赞',
        icon: HandThumbUpIcon
      }
    };

    // 处理排序
    const handleSort = value => {
      emit('update:currentSort', value);
      showSortMenu.value = false;
    };

    // 处理分类变更
    const handleCategoryChange = categoryId => {
      emit('update:currentCategory', categoryId);
    };
    const __returned__ = {
      props,
      emit,
      router,
      route,
      store,
      isAuthenticated,
      mainNavs,
      categories,
      showSortMenu,
      sortOptions,
      handleSort,
      handleCategoryChange,
      ref,
      computed,
      get useRouter() {
        return useRouter;
      },
      get useRoute() {
        return useRoute;
      },
      get useStore() {
        return useStore;
      },
      get PlusIcon() {
        return PlusIcon;
      },
      get UserIcon() {
        return UserIcon;
      },
      get ArrowsUpDownIcon() {
        return ArrowsUpDownIcon;
      },
      get ChevronDownIcon() {
        return ChevronDownIcon;
      },
      get FireIcon() {
        return FireIcon;
      },
      get ClockIcon() {
        return ClockIcon;
      },
      get HandThumbUpIcon() {
        return HandThumbUpIcon;
      },
      get EyeIcon() {
        return EyeIcon;
      },
      get HomeIcon() {
        return HomeIcon;
      },
      get DocumentIcon() {
        return DocumentIcon;
      },
      get DocumentTextIcon() {
        return DocumentTextIcon;
      },
      get IdentificationIcon() {
        return IdentificationIcon;
      },
      get BriefcaseIcon() {
        return BriefcaseIcon;
      },
      get AcademicCapIcon() {
        return AcademicCapIcon;
      },
      get LanguageIcon() {
        return LanguageIcon;
      },
      get SparklesIcon() {
        return SparklesIcon;
      },
      get UserGroupIcon() {
        return UserGroupIcon;
      }
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
