import { defineStore } from 'pinia'
import profile from '@/api/profile'

export const useProfileStore = defineStore('profile', {
  state: () => ({
    basicInfo: null,
    avatarUpdateTime: null
  }),

  getters: {
    profileAvatar: (state) => state.basicInfo?.avatar || null
  },

  actions: {
    // 更新档案头像
    updateProfileAvatar(avatarUrl) {
      if (this.basicInfo) {
        this.basicInfo = {
          ...this.basicInfo,
          avatar: avatarUrl
        }
        this.avatarUpdateTime = Date.now()
      }
    },

    // 获取档案基本信息
    async fetchBasicInfo() {
      try {
        console.log('Fetching basic info...')
        const response = await profile.getData()
        console.log('Response:', response.data)
        if (response?.data?.code === 200) {
          console.log('Previous basicInfo:', this.basicInfo)
          this.basicInfo = response.data.data.basic_info
          console.log('New basicInfo:', this.basicInfo)
          this.avatarUpdateTime = Date.now()
          console.log('Updated timestamp:', this.avatarUpdateTime)
          return this.basicInfo
        }
        return null
      } catch (error) {
        console.error('获取档案基本信息失败:', error)
        throw error
      }
    }
  }
})
