import { defineStore } from 'pinia'
import { ref } from 'vue'
import request from '@/utils/request'

export const useProfileStore = defineStore('profile', () => {
  const profile = ref(null)

  // 获取用户档案数据
  const fetchProfile = async () => {
    try {
      const response = await request.get('/api/v1/resume/profile/')
      profile.value = response.data
      return response
    } catch (error) {
      console.error('获取档案数据失败:', error)
      throw error
    }
  }

  // 更新用户档案数据
  const updateProfile = async (data) => {
    try {
      const response = await request.put('/api/v1/resume/profile/', data)
      profile.value = response.data
      return response
    } catch (error) {
      console.error('更新档案数据失败:', error)
      throw error
    }
  }

  return {
    profile,
    fetchProfile,
    updateProfile
  }
})
