          if (typeof errorData.detail === 'object') {
            errorMsg = Object.values(errorData.detail)[0];
          } else if (typeof errorData.detail === 'string') {
            errorMsg = errorData.detail;
          } else if (errorData.message) {
            errorMsg = errorData.message;
          }
