        if (phoneManager.state.countdown > 0) {
          phoneManager.state.countdown--;
        } else {
          clearInterval(phoneTimer);
          phoneTimer = null;
        }
      }, 1000);
    };

    // 在组件卸载时清理定时器
    onUnmounted(() => {
      if (phoneTimer) {
        clearInterval(phoneTimer);
        phoneTimer = null;
      }
    });

    // ... 其他代码保持不变

    const __returned__ = {
