      phoneManager,
      validatePassword,
      loading,
      handlePasswordInput,
      isPasswordFormValid,
      handlePasswordUpdate,
      strengthText,
      strengthTextClass,
      strengthColorClass,
      userInfo,
      userInfoLoading,
      fetchUserInfo,
      openPhoneModal,
      closePhoneModal,
      isPhoneCodeButtonDisabled,
      isPhoneFormValid,
      handlePhoneSendCode,
      handlePhoneUpdate,
